// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

import "../interfaces/IArbSys.sol";

library ChainUtils {
    uint256 public constant ARBITRUM_MAINNET = 42161;
    uint256 public constant ARBITRUM_GOERLI = 421613;
    IArbSys public constant ARB_SYS = IArbSys(address(100));

    function getBlockNumber() internal view returns (uint) {
        if (block.chainid == ARBITRUM_MAINNET || block.chainid == ARBITRUM_GOERLI) {
            return ARB_SYS.arbBlockNumber();
        }

        return block.number;
    }

    function getUint48BlockNumber(uint blockNumber) internal pure returns (uint48) {
        require(blockNumber <= type(uint48).max, "OVERFLOW");
        return uint48(blockNumber);
    }
}

contracts/libraries/PackingUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;


library PackingUtils {
    function pack(uint256[] memory values, uint256[] memory bitLengths) external pure returns (uint256 packed) {
        require(values.length == bitLengths.length, "Mismatch in the lengths of values and bitLengths arrays");

        uint256 currentShift;

        for (uint256 i; i < values.length; i++) {
            require(currentShift + bitLengths[i] <= 256, "Packed value exceeds 256 bits");

            uint256 maxValue = (1 << bitLengths[i]) - 1;
            require(values[i] <= maxValue, "Value too large for specified bit length");

            uint256 maskedValue = values[i] & maxValue;
            packed |= maskedValue << currentShift;
            currentShift += bitLengths[i];
        }
    }

    function unpack(uint256 packed, uint256[] memory bitLengths) external pure returns (uint256[] memory values) {
        values = new uint256[](bitLengths.length);

        uint256 currentShift;
        for (uint256 i; i < bitLengths.length; i++) {
            require(currentShift + bitLengths[i] <= 256, "Unpacked value exceeds 256 bits");

            uint256 maxValue = (1 << bitLengths[i]) - 1;
            uint256 mask = maxValue << currentShift;
            values[i] = (packed & mask) >> currentShift;

            currentShift += bitLengths[i];
        }
    }

    function unpack256To64(uint256 packed) external pure returns (uint64 a, uint64 b, uint64 c, uint64 d) {
        a = uint64(packed);
        b = uint64(packed >> 64);
        c = uint64(packed >> 128);
        d = uint64(packed >> 192);
    }

    // Function-specific unpacking utils
    function unpackExecuteNftOrder(
        uint256 packed
    ) external pure returns (uint256 a, address b, uint256 c, uint256 d, uint256 e, uint256 f) {
        a = packed & 0xFF; // 8 bits
        b = address(uint160(packed >> 8)); // 160 bits
        c = (packed >> 168) & 0xFFFF; // 16 bits
        d = (packed >> 184) & 0xFFFF; // 16 bits
        e = (packed >> 200) & 0xFFFF; // 16 bits
        f = (packed >> 216) & 0xFFFF; // 16 bits
    }
}

contracts/v6.3/TWAPPriceGetter.sol.content:
// SPDX-License-Identifier: MIT
import "@134dd3v/uniswap-v3-core-0.8-support/contracts/interfaces/IUniswapV3Pool.sol";
import "@134dd3v/uniswap-v3-core-0.8-support/contracts/libraries/TickMath.sol";
import "@134dd3v/uniswap-v3-core-0.8-support/contracts/libraries/FixedPoint96.sol";
import "@134dd3v/uniswap-v3-core-0.8-support/contracts/libraries/FullMath.sol";

pragma solidity 0.8.7;

abstract contract TWAPPriceGetter {
    // Constants
    uint32 constant MIN_TWAP_PERIOD = 1 hours / 4;
    uint32 constant MAX_TWAP_PERIOD = 4 hours;

    uint immutable precision;
    address public immutable token;

    // Adjustable parameters
    IUniswapV3Pool public uniV3Pool;
    uint32 public twapInterval;

    // State
    bool public isGnsToken0InLp;

    // Events
    event UniV3PoolUpdated(IUniswapV3Pool newValue);
    event TwapIntervalUpdated(uint32 newValue);

    constructor(IUniswapV3Pool _uniV3Pool, address _token, uint32 _twapInterval, uint _precision) {
        require(
            address(_uniV3Pool) != address(0) &&
                _twapInterval >= MIN_TWAP_PERIOD &&
                _twapInterval <= MAX_TWAP_PERIOD &&
                _precision > 0,
            "WRONG_TWAP_CONSTRUCTOR"
        );

        uniV3Pool = _uniV3Pool;
        token = _token;
        twapInterval = _twapInterval;
        precision = _precision;

        isGnsToken0InLp = uniV3Pool.token0() == _token;
    }

    // Manage variables
    function _updateUniV3Pool(IUniswapV3Pool _uniV3Pool) internal {
        require(address(_uniV3Pool) != address(0), "WRONG_VALUE");
        uniV3Pool = _uniV3Pool;
        isGnsToken0InLp = uniV3Pool.token0() == token;
        emit UniV3PoolUpdated(_uniV3Pool);
    }

    function _updateTwapInterval(uint32 _twapInterval) internal {
        require(_twapInterval >= MIN_TWAP_PERIOD && _twapInterval <= MAX_TWAP_PERIOD, "WRONG_VALUE");
        twapInterval = _twapInterval;
        emit TwapIntervalUpdated(_twapInterval);
    }

    // Returns price with "precision" decimals
    // https://github.com/Uniswap/v3-periphery/blob/main/contracts/libraries/OracleLibrary.sol
    function tokenPriceDai() public view returns (uint price) {
        uint32[] memory secondsAgos = new uint32[](2);
        secondsAgos[0] = twapInterval;
        secondsAgos[1] = 0;

        (int56[] memory tickCumulatives, ) = uniV3Pool.observe(secondsAgos);

        int56 tickCumulativesDelta = tickCumulatives[1] - tickCumulatives[0];
        int56 twapIntervalInt = int56(int32(twapInterval));

        int24 arithmeticMeanTick = int24(tickCumulativesDelta / twapIntervalInt);
        // Always round to negative infinity
        if (tickCumulativesDelta < 0 && (tickCumulativesDelta % twapIntervalInt != 0)) arithmeticMeanTick--;

        uint160 sqrtPriceX96 = TickMath.getSqrtRatioAtTick(arithmeticMeanTick);
        price = (FullMath.mulDiv(sqrtPriceX96, sqrtPriceX96, FixedPoint96.Q96) * precision) / 2 ** 96;

        if (!isGnsToken0InLp) {
            price = precision ** 2 / price;
        }
    }
}
