contracts/contracts/GTokenLockedDepositN.content:
// SPDX-License-Identifier: MIT
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol';
import '@openzeppelin/contracts/interfaces/IERC20Metadata.sol';
import '@openzeppelin/contracts/interfaces/IERC4626.sol';

import '../interfaces/IGTokenLockedDepositNftDesign.sol';

pragma solidity 0.8.7;

contract GTokenLockedDepositNft is ERC721Enumerable{
    address public immutable gToken;
    IGTokenLockedDepositNftDesign public design;

    uint8 public designDecimals;

    event DesignUpdated(IGTokenLockedDepositNftDesign newValue);
    event DesignDecimalsUpdated(uint8 newValue);

    constructor(
        string memory name,
        string memory symbol,
        address _gToken,
        IGTokenLockedDepositNftDesign _design,
        uint8 _designDecimals
    ) ERC721(name, symbol){
        gToken = _gToken;
        design = _design;
        designDecimals = _designDecimals;
    }

    modifier onlyGToken{
        require(msg.sender == gToken, "ONLY_GTOKEN");
        _;
    }

    modifier onlyGTokenManager{
        require(msg.sender == IGToken(gToken).manager(), "ONLY_MANAGER");
        _;
    }

    function updateDesign(IGTokenLockedDepositNftDesign newValue) external onlyGTokenManager{
        design = newValue;
        emit DesignUpdated(newValue);
    }

    function updateDesignDecimals(uint8 newValue) external onlyGTokenManager{
        designDecimals = newValue;
        emit DesignDecimalsUpdated(newValue);
    }

    function mint(address to, uint tokenId) external onlyGToken{
        _safeMint(to, tokenId);
    }

    function burn(uint tokenId) external onlyGToken{
        _burn(tokenId);
    }

    function tokenURI(uint tokenId) public view override returns (string memory) {
        _requireMinted(tokenId);

        return design.buildTokenURI(
            tokenId,
            IGToken(gToken).getLockedDeposit(tokenId),
            IERC20Metadata(gToken).symbol(),
            IERC20Metadata(IERC4626(gToken).asset()).symbol(),
            IERC20Metadata(gToken).decimals(),
            designDecimals
        );
    }
}

// SPDX-License-Identifier: MIT
import '@openzeppelin/contracts/utils/Base64.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '../interfaces/IGTokenLockedDepositNftDesign.sol';

pragma solidity 0.8.7;

contract GTokenLockedDepositNftDesign is IGTokenLockedDepositNftDesign{
    function buildTokenURI(
        uint tokenId,
        IGToken.LockedDeposit memory lockedDeposit,
        string memory gTokenSymbol,
        string memory assetSymbol,
        uint8 numberInputDecimals,
        uint8 numberOutputDecimals
    ) external override pure returns (string memory) {
        return string(
            abi.encodePacked(
                "data:application/json;base64,",
                Base64.encode(
                    abi.encodePacked(
                        '{"name":"', gTokenSymbol, ' Locked Deposit", "image":"data:image/svg+xml;base64,',
                        generateBase64Image(
                            tokenId,
                            lockedDeposit,
                            gTokenSymbol,
                            assetSymbol,
                            numberInputDecimals,
                            numberOutputDecimals
                        ),
                        '", "description": "This NFT represents locked ', gTokenSymbol, '."}'
                    )
                )
            )
        );
    }

    // TODO: design will evolve
    function generateBase64Image(
        uint tokenId,
        IGToken.LockedDeposit memory lockedDeposit,
        string memory gTokenSymbol,
        string memory assetSymbol,
        uint8 numberInputDecimals,
        uint8 numberOutputDecimals
    ) private pure returns(string memory) {
        return Base64.encode(
            bytes.concat(
                abi.encodePacked(
                    '<?xml version="1.0" encoding="UTF-8"?>',
                    '<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" version="1.1" ',
                    'viewBox="0 0 400 300" preserveAspectRatio="xMidYMid meet">',
                    '<style type="text/css"><![CDATA[text { font-family: Lato; font-size: 12px;}]]></style>',
                    '<rect width="400" height="300" fill="#ffffff" />',
                    '<text x="20" y="35"><tspan style="font-weight: 600">Token ID: </tspan>', Strings.toString(tokenId),
                    '</text><text x="20" y="75"><tspan style="font-weight: 600">Depositor: </tspan>',
                    Strings.toHexString(uint256(uint160(lockedDeposit.owner)), 20), '</text>'
                ),
                abi.encodePacked(
                    '<text x="20" y="115"><tspan style="font-weight: 600">Shares: </tspan>',
                    numberToRoundedString(lockedDeposit.shares, numberInputDecimals, numberOutputDecimals), ' ',
                    gTokenSymbol, '</text><text x="20" y="155"><tspan style="font-weight: 600">Assets deposited: </tspan>',
                    numberToRoundedString(lockedDeposit.assetsDeposited, numberInputDecimals, numberOutputDecimals),
                    ' ', assetSymbol, '</text>'
                ),
                abi.encodePacked(
                    '<text x="20" y="195"><tspan style="font-weight: 600">Assets discount: </tspan>',
                    numberToRoundedString(lockedDeposit.assetsDiscount, numberInputDecimals, numberOutputDecimals), ' ',
                    assetSymbol, '</text><text x="20" y="235"><tspan style="font-weight: 600">Deposit timestamp: </tspan>',
                    Strings.toString(lockedDeposit.atTimestamp), '</text>',
                    '<text x="20" y="275"><tspan style="font-weight: 600">Unlock timestamp: </tspan>',
                    Strings.toString(lockedDeposit.atTimestamp + lockedDeposit.lockDuration), '</text></svg>'
                )
            )
        );
    }

    // Returns readable string of int part of number passed
    // TODO: make it return the string with decimals = 'outputDecimals'
    function numberToRoundedString(
        uint number,
        uint8 inputDecimals,
        uint8 outputDecimals
    ) public pure returns (string memory) {
        outputDecimals = 0; // silence warning
        return Strings.toString(number / (10 ** inputDecimals));
    }
}

contracts/contracts/GTokenOpenPnlFeed.so.content:
// SPDX-License-Identifier: MIT
import '@chainlink/contracts/src/v0.8/ChainlinkClient.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

import '../interfaces/IGToken.sol';
import '../interfaces/IOwnable.sol';
import '../interfaces/IOpenTradesPnlFeed.sol';

pragma solidity 0.8.7;

contract GTokenOpenPnlFeed is ChainlinkClient, IOpenTradesPnlFeed{
    using Chainlink for Chainlink.Request;

    // Constants
    uint public immutable LINK_FEE_BALANCE_DIVIDER;
    uint constant MIN_ANSWERS = 3;
    uint constant MIN_REQUESTS_START = 1 hours;
    uint constant MAX_REQUESTS_START = 1 weeks;
    uint constant MIN_REQUESTS_EVERY = 1 hours;
    uint constant MAX_REQUESTS_EVERY = 1 days;
    uint constant MIN_REQUESTS_COUNT = 3;
    uint constant MAX_REQUESTS_COUNT = 10;

    // Params
    IGToken public immutable gToken;

    uint public requestsStart = 2 days;
    uint public requestsEvery = 6 hours;
    uint public requestsCount = 4;

    address[] public oracles;
    bytes32 public job;
    uint public minAnswers;

    // State
    int[] public nextEpochValues;
    uint public override nextEpochValuesRequestCount;
    uint public nextEpochValuesLastRequest;

    uint public lastRequestId;
    mapping(bytes32 => uint) public requestIds;   // chainlink request id => requestId
    mapping(uint => Request) public requests;     // requestId => request
    mapping(uint => int[]) public requestAnswers; // requestId => open pnl (1e18)

    struct Request{
        bool initiated;
        bool active;
        uint linkFeePerNode;
    }

    // Events
    event NumberParamUpdated(string name, uint newValue);
    event OracleUpdated(uint index, address newValue);
    event OraclesUpdated(address[] newValues);
    event JobUpdated(bytes32 newValue);

    event NextEpochValuesReset(
        uint indexed currEpoch,
        uint requestsResetCount
    );

    event NewEpochForced(uint indexed newEpoch);

    event NextEpochValueRequested(
        uint indexed currEpoch,
        uint indexed requestId,
        bytes32 job,
        uint oraclesCount,
        uint linkFeePerNode
    );

    event NewEpoch(
        uint indexed newEpoch,
        uint indexed requestId,
        int[] epochMedianValues,
        int epochAverageValue,
        uint newEpochPositiveOpenPnl
    );

    event RequestValueReceived(
        bool isLate,
        uint indexed currEpoch,
        uint indexed requestId,
        bytes32 oracleRequestId,
        address indexed oracle,
        int requestValue,
        uint linkFee
    );

    event RequestMedianValueSet(
        uint indexed currEpoch,
        uint indexed requestId,
        int[] requestValues,
        int medianValue
    );

    constructor(
        uint _LINK_FEE_BALANCE_DIVIDER,
        address _linkToken,
        IGToken _gToken,
        address[] memory _oracles,
        bytes32 _job,
        uint _minAnswers
    ){
        require(_LINK_FEE_BALANCE_DIVIDER > 0
            && _linkToken != address(0)
            && address(_gToken) != address(0)
            && _oracles.length > 0
            && _job != bytes32(0)
            && _minAnswers >= MIN_ANSWERS
            && _minAnswers % 2 == 1
            && _minAnswers <= _oracles.length / 2, "WRONG_PARAMS");

        LINK_FEE_BALANCE_DIVIDER = _LINK_FEE_BALANCE_DIVIDER;

        setChainlinkToken(_linkToken);

        gToken = _gToken;
        oracles = _oracles;
        job = _job;
        minAnswers = _minAnswers;
    }

    // Modifiers
    modifier onlyGTokenOwner { // 2-week timelock
        require(msg.sender == IOwnable(address(gToken)).owner(), "ONLY_OWNER");
        _;
    }

    modifier onlyGTokenManager { // 3-day timelock
        require(msg.sender == gToken.manager(), "ONLY_MANAGER");
        _;
    }

    modifier onlyGTokenAdmin { // bypasses timelock, emergency functions only
        require(msg.sender == gToken.admin(), "ONLY_ADMIN");
        _;
    }

    // Manage parameters
    function updateRequestsStart(uint newValue) public onlyGTokenOwner{
        require(newValue >= MIN_REQUESTS_START, "BELOW_MIN");
        require(newValue <= MAX_REQUESTS_START, "ABOVE_MAX");
        requestsStart = newValue;
        emit NumberParamUpdated("requestsStart", newValue);
    }

    function updateRequestsEvery(uint newValue) public onlyGTokenOwner{
        require(newValue >= MIN_REQUESTS_EVERY, "BELOW_MIN");
        require(newValue <= MAX_REQUESTS_EVERY, "ABOVE_MAX");
        requestsEvery = newValue;
        emit NumberParamUpdated("requestsEvery", newValue);
    }

    function updateRequestsCount(uint newValue) public onlyGTokenOwner{
        require(newValue >= MIN_REQUESTS_COUNT, "BELOW_MIN");
        require(newValue <= MAX_REQUESTS_COUNT, "ABOVE_MAX");
        requestsCount = newValue;
        emit NumberParamUpdated("requestsCount", newValue);
    }

    function updateRequestsInfoBatch(
        uint newRequestsStart,
        uint newRequestsEvery,
        uint newRequestsCount
    ) external onlyGTokenOwner{
        updateRequestsStart(newRequestsStart);
        updateRequestsEvery(newRequestsEvery);
        updateRequestsCount(newRequestsCount);
    }

    function updateMinAnswers(uint newValue) external onlyGTokenManager{
        require(newValue >= MIN_ANSWERS, "BELOW_MIN");
        require(newValue % 2 == 1, "EVEN");
        require(newValue <= oracles.length / 2, "ABOVE_MAX");
        minAnswers = newValue;
        emit NumberParamUpdated("minAnswers", newValue);
    }

    function updateOracle(uint _index, address newValue) external onlyGTokenOwner{
        require(_index < oracles.length, "INDEX_TOO_BIG");
        require(newValue != address(0), "VALUE_0");
        oracles[_index] = newValue;
        emit OracleUpdated(_index, newValue);
    }

    function updateOracles(address[] memory newValues) external onlyGTokenOwner{
        require(newValues.length >= minAnswers * 2, "ARRAY_TOO_SMALL");
        oracles = newValues;
        emit OraclesUpdated(newValues);
    }

    function updateJob(bytes32 newValue) external onlyGTokenManager{
        require(newValue != bytes32(0), "VALUE_0");
        job = newValue;
        emit JobUpdated(newValue);
    }

    // Emergency function in case of oracle manipulation
    function resetNextEpochValueRequests() external onlyGTokenAdmin{
        uint reqToResetCount = nextEpochValuesRequestCount;
        require(reqToResetCount > 0, "NO_REQUEST_TO_RESET");

        delete nextEpochValues;

        nextEpochValuesRequestCount = 0;
        nextEpochValuesLastRequest = 0;

        for(uint i; i < reqToResetCount; i++){
            requests[lastRequestId - i].active = false;
        }

        emit NextEpochValuesReset(
            gToken.currentEpoch(),
            reqToResetCount
        );
    }

    // Safety function that anyone can call in case the function above is used in an abusive manner,
    // which could theoretically delay withdrawals indefinitely since it prevents new epochs
    function forceNewEpoch() external{
        require(block.timestamp - gToken.currentEpochStart()
            >= requestsStart + requestsEvery * requestsCount,"TOO_EARLY");
        uint newEpoch = startNewEpoch();
        emit NewEpochForced(newEpoch);
    }

    // Called by gToken contract
    function newOpenPnlRequestOrEpoch() external override {
        bool firstRequest = nextEpochValuesLastRequest == 0;

        if(firstRequest
            && block.timestamp - gToken.currentEpochStart() >= requestsStart){
            makeOpenPnlRequest();

        }else if(!firstRequest
            && block.timestamp - nextEpochValuesLastRequest >= requestsEvery){
            if(nextEpochValuesRequestCount < requestsCount){
                makeOpenPnlRequest();

            }else if(nextEpochValues.length >= requestsCount){
                startNewEpoch();
            }
        }
    }

    // Create requests
    function makeOpenPnlRequest() private{
        Chainlink.Request memory linkRequest = buildChainlinkRequest(
            job,
            address(this),
            this.fulfill.selector
        );

        uint linkFeePerNode = IERC20(chainlinkTokenAddress())
            .balanceOf(address(this))
            / LINK_FEE_BALANCE_DIVIDER
            / oracles.length;

        requests[++lastRequestId] = Request({
            initiated: true,
            active: true,
            linkFeePerNode: linkFeePerNode
        });

        nextEpochValuesRequestCount++;
        nextEpochValuesLastRequest = block.timestamp;

        for(uint i; i < oracles.length; i ++){
            requestIds[sendChainlinkRequestTo(
                oracles[i],
                linkRequest,
                linkFeePerNode
            )] = lastRequestId;
        }

        emit NextEpochValueRequested(
            gToken.currentEpoch(),
            lastRequestId,
            job,
            oracles.length,
            linkFeePerNode
        );
    }

    // Handle answers
    function fulfill(
        bytes32 requestId,
        int value // 1e18
    ) external recordChainlinkFulfillment(requestId){

        uint reqId = requestIds[requestId];
        delete requestIds[requestId];

        Request memory r = requests[reqId];
        uint currentEpoch = gToken.currentEpoch();

        emit RequestValueReceived(
            !r.active,
            currentEpoch,
            reqId,
            requestId,
            msg.sender,
            value,
            r.linkFeePerNode
        );

        if(!r.active){
            return;
        }

        int[] storage answers = requestAnswers[reqId];
        answers.push(value);

        if(answers.length == minAnswers){
            int medianValue = median(answers);
            nextEpochValues.push(medianValue);

            emit RequestMedianValueSet(
                currentEpoch,
                reqId,
                answers,
                medianValue
            );

            requests[reqId].active = false;
            delete requestAnswers[reqId];
        }
    }

    // Increment epoch and update feed value
    function startNewEpoch() private returns(uint newEpoch){
        nextEpochValuesRequestCount = 0;
        nextEpochValuesLastRequest = 0;

        uint currentEpochPositiveOpenPnl = gToken.currentEpochPositiveOpenPnl();

        // If all responses arrived, use mean, otherwise it means we forced a new epoch,
        // so as a safety we use the last epoch value
        int newEpochOpenPnl = nextEpochValues.length >= requestsCount ?
            average(nextEpochValues) : int(currentEpochPositiveOpenPnl);

        uint finalNewEpochPositiveOpenPnl = gToken.updateAccPnlPerTokenUsed(
            currentEpochPositiveOpenPnl,
            newEpochOpenPnl > 0 ? uint(newEpochOpenPnl) : 0
        );

        newEpoch = gToken.currentEpoch();

        emit NewEpoch(
            newEpoch,
            lastRequestId,
            nextEpochValues,
            newEpochOpenPnl,
            finalNewEpochPositiveOpenPnl
        );

        delete nextEpochValues;
    }

    // Median function
    function swap(int[] memory array, uint i, uint j) private pure{
        (array[i], array[j]) = (array[j], array[i]);
    }

    function sort(int[] memory array, uint begin, uint end) private pure{
        if (begin >= end) { return; }

        uint j = begin;
        int pivot = array[j];

        for (uint i = begin + 1; i < end; ++i) {
            if (array[i] < pivot) {
                swap(array, i, ++j);
            }
        }

        swap(array, begin, j);
        sort(array, begin, j);
        sort(array, j + 1, end);
    }

    function median(int[] memory array) private pure returns(int){
        sort(array, 0, array.length);

        return array.length % 2 == 0 ?
            (array[array.length / 2 - 1] + array[array.length / 2]) / 2 :
            array[array.length / 2];
    }

    // Average function
    function average(int[] memory array) private pure returns(int){
        int sum;
        for(uint i; i < array.length; i++){
            sum += array[i];
        }

        return sum / int(array.length);
    }
}

contracts/contracts/GTokenV6_3_2.sol.content:
// SPDX-License-Identifier: MIT
import "../utils/ERC20Upgradeable.sol";
import "../utils/IERC20Upgradeable.sol";
import "../utils/ERC4626Upgradeable.sol";
import "../utils/MathUpgradeable.sol";
import "../utils/OwnableUpgradeable.sol";

import "../interfaces/IGToken.sol";
import "../interfaces/IGnsToken.sol";
import "../interfaces/INft.sol";
import "../interfaces/IOpenTradesPnlFeed.sol";
import "../libraries/ChainUtils.sol";

pragma solidity 0.8.7;

contract GTokenV6_3_2 is IERC20Upgradeable, ERC20Upgradeable, ERC4626Upgradeable, OwnableUpgradeable, IGToken {
    using MathUpgradeable for uint;

    // Contracts & Addresses (constant)
    address public gnsToken;
    INft public lockedDepositNft;

    // Contracts & Addresses (adjustable)
    address public override manager; // 3-day timelock contract
    address public override admin; // bypasses timelock, access to emergency functions

    address public pnlHandler;
    IOpenTradesPnlFeed public openTradesPnlFeed;
    GnsPriceProvider public gnsPriceProvider;

    struct GnsPriceProvider {
        address addr;
        bytes signature;
    }

    // Parameters (constant)
    uint constant PRECISION = 1e18; // 18 decimals (acc values & price)
    uint constant PRECISION_2 = 1e40; // 40 decimals (acc block weighted market cap)
    uint constant GNS_PRECISION = 1e10; // 10 decimals (gns/asset oracle)
    uint constant MIN_DAILY_ACC_PNL_DELTA = PRECISION / 10; // 0.1 (price delta)
    uint constant MAX_SUPPLY_INCREASE_DAILY_P = 50 * PRECISION; // 50% / day (when under collat)
    uint constant MAX_LOSSES_BURN_P = 25 * PRECISION; // 25% of all losses
    uint constant MAX_GNS_SUPPLY_MINT_DAILY_P = PRECISION / 20; // 0.05% / day (18.25% / yr max)
    uint constant MAX_DISCOUNT_P = 10 * PRECISION; // 10%
    uint public MIN_LOCK_DURATION; // min locked asset deposit duration
    uint constant MAX_LOCK_DURATION = 365 days; // max locked asset deposit duration
    uint[] WITHDRAW_EPOCHS_LOCKS; // epochs withdraw locks at over collat thresholds

    // Parameters (adjustable)
    uint public maxAccOpenPnlDelta; // PRECISION (max price delta on new epochs from open pnl)
    uint public maxDailyAccPnlDelta; // PRECISION (max daily price delta from closed pnl)
    uint[2] public withdrawLockThresholdsP; // PRECISION (% of over collat, used with WITHDRAW_EPOCHS_LOCKS)
    uint public maxSupplyIncreaseDailyP; // PRECISION (% per day, when under collat)
    uint public lossesBurnP; // PRECISION (% of all losses)
    uint public maxGnsSupplyMintDailyP; // PRECISION (% of gns supply)
    uint public maxDiscountP; // PRECISION (%, maximum discount for locked deposits)
    uint public maxDiscountThresholdP; // PRECISION (maximum collat %, for locked deposits)

    // Price state
    uint public shareToAssetsPrice; // PRECISION
    int public accPnlPerTokenUsed; // PRECISION (snapshot of accPnlPerToken)
    int public accPnlPerToken; // PRECISION (updated in real-time)
    uint public accRewardsPerToken; // PRECISION

    // Closed Pnl state
    int public dailyAccPnlDelta; // PRECISION
    uint public lastDailyAccPnlDeltaReset; // timestamp

    // Epochs state (withdrawals)
    uint public override currentEpoch; // global id
    uint public override currentEpochStart; // timestamp
    uint public override currentEpochPositiveOpenPnl; // 1e18

    // Deposit / Withdraw state
    uint public currentMaxSupply; // 1e18
    uint public lastMaxSupplyUpdate; // timestamp
    mapping(address => mapping(uint => uint)) public withdrawRequests; // owner => unlock epoch => shares

    // Locked deposits state
    uint public lockedDepositsCount; // global id
    mapping(uint => LockedDeposit) public lockedDeposits;

    // Deplete / Refill state
    uint public assetsToDeplete; // 1e18
    // uint public dailyMintedGns; // 1e18
    // uint public lastDailyMintedGnsReset; // timestamp

    // Statistics (not used for contract logic)
    uint public totalDeposited; // 1e18 (assets)
    int public totalClosedPnl; // 1e18 (assets)
    uint public totalRewards; // 1e18 (assets)
    int public totalLiability; // 1e18 (assets)
    uint public totalLockedDiscounts; // 1e18 (assets)
    uint public totalDiscounts; // 1e18 (assets)
    uint public totalDepleted; // 1e18 (assets)
    uint public totalRefilled; // 1e18 (assets)

    // Useful acc values
    uint public accBlockWeightedMarketCap; // 1e40, acc sum of (blocks elapsed / market cap)
    uint public accBlockWeightedMarketCapLastStored; // block

    // Events
    event AddressParamUpdated(string name, address newValue);
    event GnsPriceProviderUpdated(GnsPriceProvider newValue);
    event NumberParamUpdated(string name, uint newValue);
    event WithdrawLockThresholdsPUpdated(uint[2] newValue);

    event CurrentMaxSupplyUpdated(uint newValue);
    event DailyAccPnlDeltaReset();
    event ShareToAssetsPriceUpdated(uint newValue);
    event OpenTradesPnlFeedCallFailed();

    event WithdrawRequested(
        address indexed sender,
        address indexed owner,
        uint shares,
        uint currEpoch,
        uint indexed unlockEpoch
    );
    event WithdrawCanceled(
        address indexed sender,
        address indexed owner,
        uint shares,
        uint currEpoch,
        uint indexed unlockEpoch
    );

    event DepositLocked(address indexed sender, address indexed owner, uint depositId, LockedDeposit d);
    event DepositUnlocked(
        address indexed sender,
        address indexed receiver,
        address indexed owner,
        uint depositId,
        LockedDeposit d
    );

    event RewardDistributed(address indexed sender, uint assets);

    event AssetsSent(address indexed sender, address indexed receiver, uint assets);
    event AssetsReceived(address indexed sender, address indexed user, uint assets, uint assetsLessDeplete);

    event Depleted(address indexed sender, uint assets, uint amountGns);
    event Refilled(address indexed sender, uint assets, uint amountGns);

    event AccPnlPerTokenUsedUpdated(
        address indexed sender,
        uint indexed newEpoch,
        uint prevPositiveOpenPnl,
        uint newPositiveOpenPnl,
        uint newEpochPositiveOpenPnl,
        int newAccPnlPerTokenUsed
    );

    event AccBlockWeightedMarketCapStored(uint newAccValue);

    // Prevent stack too deep error
    struct ContractAddresses {
        address asset;
        address owner; // 2-week timelock contract
        address manager; // 3-day timelock contract
        address admin; // bypasses timelock, access to emergency functions
        address gnsToken;
        address lockedDepositNft;
        address pnlHandler;
        address openTradesPnlFeed;
        GnsPriceProvider gnsPriceProvider;
    }

    // Initializer function called when this contract is deployed
    function initialize(
        string memory _name,
        string memory _symbol,
        ContractAddresses memory _contractAddresses,
        uint _MIN_LOCK_DURATION,
        uint _maxAccOpenPnlDelta,
        uint _maxDailyAccPnlDelta,
        uint[2] memory _withdrawLockThresholdsP,
        uint _maxSupplyIncreaseDailyP,
        uint _lossesBurnP,
        uint _maxGnsSupplyMintDailyP,
        uint _maxDiscountP,
        uint _maxDiscountThresholdP
    ) external initializer {
        require(
            _contractAddresses.asset != address(0) &&
                _contractAddresses.owner != address(0) &&
                _contractAddresses.manager != address(0) &&
                _contractAddresses.admin != address(0) &&
                _contractAddresses.owner != _contractAddresses.manager &&
                _contractAddresses.manager != _contractAddresses.admin &&
                _contractAddresses.lockedDepositNft != address(0) &&
                _contractAddresses.pnlHandler != address(0) &&
                _contractAddresses.openTradesPnlFeed != address(0) &&
                _maxDailyAccPnlDelta >= MIN_DAILY_ACC_PNL_DELTA &&
                _withdrawLockThresholdsP[1] > _withdrawLockThresholdsP[0] &&
                _maxSupplyIncreaseDailyP <= MAX_SUPPLY_INCREASE_DAILY_P &&
                _lossesBurnP <= MAX_LOSSES_BURN_P &&
                _maxGnsSupplyMintDailyP <= MAX_GNS_SUPPLY_MINT_DAILY_P &&
                _maxDiscountP <= MAX_DISCOUNT_P &&
                _maxDiscountThresholdP >= 100 * PRECISION,
            "WRONG_PARAMS"
        );

        __ERC20_init(_name, _symbol);
        __ERC4626_init(IERC20MetadataUpgradeable(_contractAddresses.asset));
        _transferOwnership(_contractAddresses.owner);

        gnsToken = _contractAddresses.gnsToken;
        lockedDepositNft = INft(_contractAddresses.lockedDepositNft);
        manager = _contractAddresses.manager;
        admin = _contractAddresses.admin;
        pnlHandler = _contractAddresses.pnlHandler;
        openTradesPnlFeed = IOpenTradesPnlFeed(_contractAddresses.openTradesPnlFeed);
        gnsPriceProvider = _contractAddresses.gnsPriceProvider;

        MIN_LOCK_DURATION = _MIN_LOCK_DURATION;

        maxAccOpenPnlDelta = _maxAccOpenPnlDelta;
        maxDailyAccPnlDelta = _maxDailyAccPnlDelta;
        withdrawLockThresholdsP = _withdrawLockThresholdsP;
        maxSupplyIncreaseDailyP = _maxSupplyIncreaseDailyP;
        lossesBurnP = _lossesBurnP;
        maxDiscountP = _maxDiscountP;
        maxDiscountThresholdP = _maxDiscountThresholdP;

        shareToAssetsPrice = PRECISION;
        currentEpoch = 1;
        currentEpochStart = block.timestamp;
        WITHDRAW_EPOCHS_LOCKS = [3, 2, 1];
    }

    function initializeV2() external reinitializer(2) {
        storeAccBlockWeightedMarketCap();
        totalDeposited += totalRewards; // include all already distributed rewards in totalDeposited
    }

    // Modifiers
    modifier onlyManager() {
        require(_msgSender() == manager, "ONLY_MANAGER");
        _;
    }

    modifier checks(uint assetsOrShares) {
        require(shareToAssetsPrice > 0, "PRICE_0");
        require(assetsOrShares > 0, "VALUE_0");
        _;
    }

    modifier validDiscount(uint lockDuration) {
        require(maxDiscountP > 0, "NO_ACTIVE_DISCOUNT");
        require(lockDuration >= MIN_LOCK_DURATION, "BELOW_MIN_LOCK_DURATION");
        require(lockDuration <= MAX_LOCK_DURATION, "ABOVE_MAX_LOCK_DURATION");
        _;
    }

    // Manage addresses
    function transferOwnership(address newOwner) public override onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        require(newOwner != manager && newOwner != admin, "WRONG_VALUE");
        _transferOwnership(newOwner);
    }

    function updateManager(address newValue) external onlyOwner {
        require(newValue != address(0), "ADDRESS_0");
        require(newValue != owner() && newValue != admin, "WRONG_VALUE");
        manager = newValue;
        emit AddressParamUpdated("manager", newValue);
    }

    function updateAdmin(address newValue) external onlyManager {
        require(newValue != address(0), "ADDRESS_0");
        require(newValue != owner() && newValue != manager, "WRONG_VALUE");
        admin = newValue;
        emit AddressParamUpdated("admin", newValue);
    }

    function updatePnlHandler(address newValue) external onlyOwner {
        require(newValue != address(0), "ADDRESS_0");
        pnlHandler = newValue;
        emit AddressParamUpdated("pnlHandler", newValue);
    }

    function updateGnsPriceProvider(GnsPriceProvider memory newValue) external onlyManager {
        require(newValue.addr != address(0), "ADDRESS_0");
        require(newValue.signature.length > 0, "BYTES_0");
        gnsPriceProvider = newValue;
        emit GnsPriceProviderUpdated(newValue);
    }

    function updateOpenTradesPnlFeed(address newValue) external onlyOwner {
        require(newValue != address(0), "ADDRESS_0");
        openTradesPnlFeed = IOpenTradesPnlFeed(newValue);
        emit AddressParamUpdated("openTradesPnlFeed", newValue);
    }

    // Manage parameters
    function updateMaxAccOpenPnlDelta(uint newValue) external onlyOwner {
        maxAccOpenPnlDelta = newValue;
        emit NumberParamUpdated("maxAccOpenPnlDelta", newValue);
    }

    function updateMaxDailyAccPnlDelta(uint newValue) external onlyManager {
        require(newValue >= MIN_DAILY_ACC_PNL_DELTA, "BELOW_MIN");
        maxDailyAccPnlDelta = newValue;
        emit NumberParamUpdated("maxDailyAccPnlDelta", newValue);
    }

    function updateWithdrawLockThresholdsP(uint[2] memory newValue) external onlyOwner {
        require(newValue[1] > newValue[0], "WRONG_VALUES");
        withdrawLockThresholdsP = newValue;
        emit WithdrawLockThresholdsPUpdated(newValue);
    }

    function updateMaxSupplyIncreaseDailyP(uint newValue) external onlyManager {
        require(newValue <= MAX_SUPPLY_INCREASE_DAILY_P, "ABOVE_MAX");
        maxSupplyIncreaseDailyP = newValue;
        emit NumberParamUpdated("maxSupplyIncreaseDailyP", newValue);
    }

    function updateLossesBurnP(uint newValue) external onlyManager {
        require(newValue <= MAX_LOSSES_BURN_P, "ABOVE_MAX");
        lossesBurnP = newValue;
        emit NumberParamUpdated("lossesBurnP", newValue);
    }

    function updateMaxGnsSupplyMintDailyP(uint newValue) external onlyManager {
        require(newValue <= MAX_GNS_SUPPLY_MINT_DAILY_P, "ABOVE_MAX");
        maxGnsSupplyMintDailyP = newValue;
        emit NumberParamUpdated("maxGnsSupplyMintDailyP", newValue);
    }

    function updateMaxDiscountP(uint newValue) external onlyManager {
        require(newValue <= MAX_DISCOUNT_P, "ABOVE_MAX_DISCOUNT");
        maxDiscountP = newValue;
        emit NumberParamUpdated("maxDiscountP", newValue);
    }

    function updateMaxDiscountThresholdP(uint newValue) external onlyManager {
        require(newValue >= 100 * PRECISION, "BELOW_MIN");
        maxDiscountThresholdP = newValue;
        emit NumberParamUpdated("maxDiscountThresholdP", newValue);
    }

    // View helper functions
    function maxAccPnlPerToken() public view returns (uint) {
        // PRECISION
        return PRECISION + accRewardsPerToken;
    }

    function collateralizationP() public view returns (uint) {
        // PRECISION (%)
        uint _maxAccPnlPerToken = maxAccPnlPerToken();
        return
            ((
                accPnlPerTokenUsed > 0
                    ? (_maxAccPnlPerToken - uint(accPnlPerTokenUsed))
                    : (_maxAccPnlPerToken + uint(accPnlPerTokenUsed * (-1)))
            ) *
                100 *
                PRECISION) / _maxAccPnlPerToken;
    }

    function gnsTokenToAssetsPrice() public view returns (uint price) {
        // GNS_PRECISION
        (bool success, bytes memory result) = gnsPriceProvider.addr.staticcall(gnsPriceProvider.signature);

        require(success == true, "GNS_PRICE_CALL_FAILED");
        (price) = abi.decode(result, (uint));

        require(price > 0, "GNS_TOKEN_PRICE_0");
    }

    function withdrawEpochsTimelock() public view returns (uint) {
        uint collatP = collateralizationP();
        uint overCollatP = (collatP - MathUpgradeable.min(collatP, 100 * PRECISION));

        return
            overCollatP > withdrawLockThresholdsP[1]
                ? WITHDRAW_EPOCHS_LOCKS[2]
                : (overCollatP > withdrawLockThresholdsP[0] ? WITHDRAW_EPOCHS_LOCKS[1] : WITHDRAW_EPOCHS_LOCKS[0]);
    }

    function lockDiscountP(uint collatP, uint lockDuration) public view returns (uint) {
        return
            ((
                collatP <= 100 * PRECISION
                    ? maxDiscountP
                    : (
                        collatP <= maxDiscountThresholdP
                            ? (maxDiscountP * (maxDiscountThresholdP - collatP)) /
                                (maxDiscountThresholdP - 100 * PRECISION)
                            : 0
                    )
            ) * lockDuration) / MAX_LOCK_DURATION;
    }

    function totalSharesBeingWithdrawn(address owner) public view returns (uint shares) {
        for (uint i = currentEpoch; i <= currentEpoch + WITHDRAW_EPOCHS_LOCKS[0]; i++) {
            shares += withdrawRequests[owner][i];
        }
    }

    function getPendingAccBlockWeightedMarketCap(uint currentBlock) public view override returns (uint) {
        return
            accBlockWeightedMarketCap +
            ((currentBlock - accBlockWeightedMarketCapLastStored) * PRECISION_2) /
            MathUpgradeable.max(marketCap(), 1);
    }

    // Public helper functions
    function tryUpdateCurrentMaxSupply() public {
        if (block.timestamp - lastMaxSupplyUpdate >= 24 hours) {
            currentMaxSupply = (totalSupply() * (PRECISION * 100 + maxSupplyIncreaseDailyP)) / (PRECISION * 100);
            lastMaxSupplyUpdate = block.timestamp;

            emit CurrentMaxSupplyUpdated(currentMaxSupply);
        }
    }

    function tryResetDailyAccPnlDelta() public {
        if (block.timestamp - lastDailyAccPnlDeltaReset >= 24 hours) {
            dailyAccPnlDelta = 0;
            lastDailyAccPnlDeltaReset = block.timestamp;

            emit DailyAccPnlDeltaReset();
        }
    }

    function tryNewOpenPnlRequestOrEpoch() public {
        // Fault tolerance so that activity can continue anyway
        (bool success, ) = address(openTradesPnlFeed).call(abi.encodeWithSignature("newOpenPnlRequestOrEpoch()"));
        if (!success) {
            emit OpenTradesPnlFeedCallFailed();
        }
    }

    function storeAccBlockWeightedMarketCap() public {
        uint currentBlock = ChainUtils.getBlockNumber();
        accBlockWeightedMarketCap = getPendingAccBlockWeightedMarketCap(currentBlock);
        accBlockWeightedMarketCapLastStored = currentBlock;

        emit AccBlockWeightedMarketCapStored(accBlockWeightedMarketCap);
    }

    // Private helper functions
    function updateShareToAssetsPrice() private {
        storeAccBlockWeightedMarketCap();

        shareToAssetsPrice = maxAccPnlPerToken() - (accPnlPerTokenUsed > 0 ? uint(accPnlPerTokenUsed) : uint(0)); // PRECISION
        emit ShareToAssetsPriceUpdated(shareToAssetsPrice);
    }

    function _assetIERC20() private view returns (IERC20Upgradeable) {
        return IERC20Upgradeable(asset());
    }

    // Override ERC-20 functions (prevent sending to address that is withdrawing)
    function transfer(address to, uint amount) public override(IERC20Upgradeable, ERC20Upgradeable) returns (bool) {
        address sender = _msgSender();
        require(totalSharesBeingWithdrawn(sender) <= balanceOf(sender) - amount, "PENDING_WITHDRAWAL");
        _transfer(sender, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint amount
    ) public override(IERC20Upgradeable, ERC20Upgradeable) returns (bool) {
        require(totalSharesBeingWithdrawn(from) <= balanceOf(from) - amount, "PENDING_WITHDRAWAL");
        _spendAllowance(from, _msgSender(), amount);
        _transfer(from, to, amount);
        return true;
    }

    // Override ERC-4626 view functions
    function decimals() public view override(ERC20Upgradeable, ERC4626Upgradeable) returns (uint8) {
        return ERC4626Upgradeable.decimals();
    }

    function _convertToShares(
        uint assets,
        MathUpgradeable.Rounding rounding
    ) internal view override returns (uint shares) {
        return assets.mulDiv(PRECISION, shareToAssetsPrice, rounding);
    }

    function _convertToAssets(
        uint shares,
        MathUpgradeable.Rounding rounding
    ) internal view override returns (uint assets) {
        // Prevent overflow when called from maxDeposit with maxMint = uint.max
        if (shares == type(uint).max && shareToAssetsPrice >= PRECISION) {
            return shares;
        }
        return shares.mulDiv(shareToAssetsPrice, PRECISION, rounding);
    }

    function maxMint(address) public view override returns (uint) {
        return
            accPnlPerTokenUsed > 0
                ? currentMaxSupply - MathUpgradeable.min(currentMaxSupply, totalSupply())
                : type(uint).max;
    }

    function maxDeposit(address owner) public view override returns (uint) {
        return _convertToAssets(maxMint(owner), MathUpgradeable.Rounding.Down);
    }

    function maxRedeem(address owner) public view override returns (uint) {
        return
            openTradesPnlFeed.nextEpochValuesRequestCount() == 0
                ? MathUpgradeable.min(withdrawRequests[owner][currentEpoch], totalSupply() - 1)
                : 0;
    }

    function maxWithdraw(address owner) public view override returns (uint) {
        return _convertToAssets(maxRedeem(owner), MathUpgradeable.Rounding.Down);
    }

    // Override ERC-4626 interactions (call scaleVariables on every deposit / withdrawal)
    function deposit(uint assets, address receiver) public override checks(assets) returns (uint) {
        require(assets <= maxDeposit(receiver), "ERC4626: deposit more than max");

        uint shares = previewDeposit(assets);
        scaleVariables(shares, assets, true);

        _deposit(_msgSender(), receiver, assets, shares);
        return shares;
    }

    function mint(uint shares, address receiver) public override checks(shares) returns (uint) {
        require(shares <= maxMint(receiver), "ERC4626: mint more than max");

        uint assets = previewMint(shares);
        scaleVariables(shares, assets, true);

        _deposit(_msgSender(), receiver, assets, shares);
        return assets;
    }

    function withdraw(uint assets, address receiver, address owner) public override checks(assets) returns (uint) {
        require(assets <= maxWithdraw(owner), "ERC4626: withdraw more than max");

        uint shares = previewWithdraw(assets);
        withdrawRequests[owner][currentEpoch] -= shares;

        scaleVariables(shares, assets, false);

        _withdraw(_msgSender(), receiver, owner, assets, shares);
        return shares;
    }

    function redeem(uint shares, address receiver, address owner) public override checks(shares) returns (uint) {
        require(shares <= maxRedeem(owner), "ERC4626: redeem more than max");

        withdrawRequests[owner][currentEpoch] -= shares;

        uint assets = previewRedeem(shares);
        scaleVariables(shares, assets, false);

        _withdraw(_msgSender(), receiver, owner, assets, shares);
        return assets;
    }

    function scaleVariables(uint shares, uint assets, bool isDeposit) private {
        uint supply = totalSupply();

        if (accPnlPerToken < 0) {
            accPnlPerToken = (accPnlPerToken * int(supply)) / (isDeposit ? int(supply + shares) : int(supply - shares));
        } else if (accPnlPerToken > 0) {
            totalLiability += ((int(shares) * totalLiability) / int(supply)) * (isDeposit ? int(1) : int(-1));
        }

        totalDeposited = isDeposit ? totalDeposited + assets : totalDeposited - assets;

        storeAccBlockWeightedMarketCap();
    }

    // Withdraw requests (need to be done before calling 'withdraw' / 'redeem')
    function makeWithdrawRequest(uint shares, address owner) external {
        require(openTradesPnlFeed.nextEpochValuesRequestCount() == 0, "END_OF_EPOCH");

        address sender = _msgSender();
        uint allowance = allowance(owner, sender);
        require(sender == owner || (allowance > 0 && allowance >= shares), "NOT_ALLOWED");

        require(totalSharesBeingWithdrawn(owner) + shares <= balanceOf(owner), "MORE_THAN_BALANCE");

        uint unlockEpoch = currentEpoch + withdrawEpochsTimelock();
        withdrawRequests[owner][unlockEpoch] += shares;

        emit WithdrawRequested(sender, owner, shares, currentEpoch, unlockEpoch);
    }

    function cancelWithdrawRequest(uint shares, address owner, uint unlockEpoch) external {
        require(shares <= withdrawRequests[owner][unlockEpoch], "MORE_THAN_WITHDRAW_AMOUNT");

        address sender = _msgSender();
        uint allowance = allowance(owner, sender);
        require(sender == owner || (allowance > 0 && allowance >= shares), "NOT_ALLOWED");

        withdrawRequests[owner][unlockEpoch] -= shares;

        emit WithdrawCanceled(sender, owner, shares, currentEpoch, unlockEpoch);
    }

    // Locked and discounted deposits
    function depositWithDiscountAndLock(
        uint assets,
        uint lockDuration,
        address receiver
    ) external checks(assets) validDiscount(lockDuration) returns (uint) {
        uint simulatedAssets = (assets * (PRECISION * 100 + lockDiscountP(collateralizationP(), lockDuration))) /
            (PRECISION * 100);

        require(simulatedAssets <= maxDeposit(receiver), "DEPOSIT_MORE_THAN_MAX");

        return
            _executeDiscountAndLock(simulatedAssets, assets, previewDeposit(simulatedAssets), lockDuration, receiver);
    }

    function mintWithDiscountAndLock(
        uint shares,
        uint lockDuration,
        address receiver
    ) external checks(shares) validDiscount(lockDuration) returns (uint) {
        require(shares <= maxMint(receiver), "MINT_MORE_THAN_MAX");
        uint assets = previewMint(shares);

        return
            _executeDiscountAndLock(
                assets,
                (assets * (PRECISION * 100)) / (PRECISION * 100 + lockDiscountP(collateralizationP(), lockDuration)),
                shares,
                lockDuration,
                receiver
            );
    }

    function _executeDiscountAndLock(
        uint assets,
        uint assetsDeposited,
        uint shares,
        uint lockDuration,
        address receiver
    ) private returns (uint) {
        require(assets > assetsDeposited, "NO_DISCOUNT");

        uint depositId = ++lockedDepositsCount;
        uint assetsDiscount = assets - assetsDeposited;

        LockedDeposit storage d = lockedDeposits[depositId];
        d.owner = receiver;
        d.shares = shares;
        d.assetsDeposited = assetsDeposited;
        d.assetsDiscount = assetsDiscount;
        d.atTimestamp = block.timestamp;
        d.lockDuration = lockDuration;

        scaleVariables(shares, assetsDeposited, true);
        address sender = _msgSender();
        _deposit(sender, address(this), assetsDeposited, shares);

        totalDiscounts += assetsDiscount;
        totalLockedDiscounts += assetsDiscount;

        lockedDepositNft.mint(receiver, depositId);

        emit DepositLocked(sender, d.owner, depositId, d);
        return depositId;
    }

    function unlockDeposit(uint depositId, address receiver) external {
        LockedDeposit storage d = lockedDeposits[depositId];

        address sender = _msgSender();
        address owner = lockedDepositNft.ownerOf(depositId);

        require(
            owner == sender ||
                lockedDepositNft.getApproved(depositId) == sender ||
                lockedDepositNft.isApprovedForAll(owner, sender),
            "NOT_ALLOWED"
        );
        require(block.timestamp >= d.atTimestamp + d.lockDuration, "NOT_UNLOCKED");

        int accPnlDelta = int(d.assetsDiscount.mulDiv(PRECISION, totalSupply(), MathUpgradeable.Rounding.Up));

        accPnlPerToken += accPnlDelta;
        require(accPnlPerToken <= int(maxAccPnlPerToken()), "NOT_ENOUGH_ASSETS");

        lockedDepositNft.burn(depositId);

        accPnlPerTokenUsed += accPnlDelta;
        updateShareToAssetsPrice();

        totalLiability += int(d.assetsDiscount);
        totalLockedDiscounts -= d.assetsDiscount;

        _transfer(address(this), receiver, d.shares);

        emit DepositUnlocked(sender, receiver, owner, depositId, d);
    }

    // Distributes a reward evenly to all stakers of the vault
    function distributeReward(uint assets) override external {
        address sender = _msgSender();
        SafeERC20Upgradeable.safeTransferFrom(_assetIERC20(), sender, address(this), assets);

        accRewardsPerToken += (assets * PRECISION) / totalSupply();
        updateShareToAssetsPrice();

        totalRewards += assets;
        totalDeposited += assets;

        emit RewardDistributed(sender, assets);
    }

    // PnL interactions (happens often, so also used to trigger other actions)
    function sendAssets(uint assets, address receiver) override external {
        address sender = _msgSender();
        require(sender == pnlHandler, "ONLY_TRADING_PNL_HANDLER");

        int accPnlDelta = int(assets.mulDiv(PRECISION, totalSupply(), MathUpgradeable.Rounding.Up));

        accPnlPerToken += accPnlDelta;
        require(accPnlPerToken <= int(maxAccPnlPerToken()), "NOT_ENOUGH_ASSETS");

        tryResetDailyAccPnlDelta();
        dailyAccPnlDelta += accPnlDelta;
        require(dailyAccPnlDelta <= int(maxDailyAccPnlDelta), "MAX_DAILY_PNL");

        totalLiability += int(assets);
        totalClosedPnl += int(assets);

        tryNewOpenPnlRequestOrEpoch();
        tryUpdateCurrentMaxSupply();

        SafeERC20Upgradeable.safeTransfer(_assetIERC20(), receiver, assets);

        emit AssetsSent(sender, receiver, assets);
    }

    function receiveAssets(uint assets, address user) override external {
        address sender = _msgSender();
        SafeERC20Upgradeable.safeTransferFrom(_assetIERC20(), sender, address(this), assets);

        uint assetsLessDeplete = assets;

        if (accPnlPerTokenUsed < 0 && accPnlPerToken < 0) {
            uint depleteAmount = (assets * lossesBurnP) / PRECISION / 100;
            assetsToDeplete += depleteAmount;
            assetsLessDeplete -= depleteAmount;
        }

        int accPnlDelta = int((assetsLessDeplete * PRECISION) / totalSupply());
        accPnlPerToken -= accPnlDelta;

        tryResetDailyAccPnlDelta();
        dailyAccPnlDelta -= accPnlDelta;

        totalLiability -= int(assetsLessDeplete);
        totalClosedPnl -= int(assetsLessDeplete);

        tryNewOpenPnlRequestOrEpoch();
        tryUpdateCurrentMaxSupply();

        emit AssetsReceived(sender, user, assets, assetsLessDeplete);
    }

    // GNS mint / burn mechanism
    function deplete(uint assets) external {
        require(assets <= assetsToDeplete, "AMOUNT_TOO_BIG");
        assetsToDeplete -= assets;

        address sender = _msgSender();
        totalDepleted += assets;

        SafeERC20Upgradeable.safeTransfer(_assetIERC20(), sender, assets);
        emit Depleted(sender, assets, 0);
    }

    function refill(uint assets) external {
        require(accPnlPerTokenUsed > 0, "NOT_UNDER_COLLATERALIZED");

        uint supply = totalSupply();
        require(assets <= (uint(accPnlPerTokenUsed) * supply) / PRECISION, "AMOUNT_TOO_BIG");

        address sender = _msgSender();
        SafeERC20Upgradeable.safeTransferFrom(_assetIERC20(), sender, address(this), assets);

        int accPnlDelta = int((assets * PRECISION) / supply);
        accPnlPerToken -= accPnlDelta;
        accPnlPerTokenUsed -= accPnlDelta;
        updateShareToAssetsPrice();

        totalRefilled += assets;
        emit Refilled(sender, assets, 0);
    }

    // Updates shareToAssetsPrice based on the new PnL and starts a new epoch
    function updateAccPnlPerTokenUsed(
        uint prevPositiveOpenPnl, // 1e18
        uint newPositiveOpenPnl // 1e18
    ) external override returns (uint) {
        address sender = _msgSender();
        require(sender == address(openTradesPnlFeed), "ONLY_PNL_FEED");

        int delta = int(newPositiveOpenPnl) - int(prevPositiveOpenPnl); // 1e18
        uint supply = totalSupply();

        int maxDelta = int(
            MathUpgradeable.min(
                (uint(int(maxAccPnlPerToken()) - accPnlPerToken) * supply) / PRECISION,
                (maxAccOpenPnlDelta * supply) / PRECISION
            )
        ); // 1e18

        delta = delta > maxDelta ? maxDelta : delta;

        accPnlPerToken += (delta * int(PRECISION)) / int(supply);
        totalLiability += delta;

        accPnlPerTokenUsed = accPnlPerToken;
        updateShareToAssetsPrice();

        currentEpoch++;
        currentEpochStart = block.timestamp;
        currentEpochPositiveOpenPnl = uint(int(prevPositiveOpenPnl) + delta);

        tryUpdateCurrentMaxSupply();

        emit AccPnlPerTokenUsedUpdated(
            sender,
            currentEpoch,
            prevPositiveOpenPnl,
            newPositiveOpenPnl,
            currentEpochPositiveOpenPnl,
            accPnlPerTokenUsed
        );

        return currentEpochPositiveOpenPnl;
    }

    // Getters
    function getLockedDeposit(uint depositId) external view override returns (LockedDeposit memory) {
        return lockedDeposits[depositId];
    }

    function tvl() public view override returns (uint) {
        return (maxAccPnlPerToken() * totalSupply()) / PRECISION; // 1e18
    }

    function availableAssets() public view returns (uint) {
        return (uint(int(maxAccPnlPerToken()) - accPnlPerTokenUsed) * totalSupply()) / PRECISION; // 1e18
    }

    // To be compatible with old pairs storage contract v6 (to be used only with gDAI vault)
    function currentBalanceDai() external view override returns (uint) {
        return availableAssets(); // 1e18
    }

    function marketCap() public view override returns (uint) {
        return (totalSupply() * shareToAssetsPrice) / PRECISION; // 1e18
    }
}

contracts/interfaces/IArbSys.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.9.0;

/**
 * @title Precompiled contract that exists in every Arbitrum chain at address(100), 0x0000000000000000000000000000000000000064. Exposes a variety of system-level functionality.
 */
interface IArbSys {
    /**
     * @notice Get internal version number identifying an ArbOS build
     * @return version number as int
     */
    function arbOSVersion() external pure returns (uint);

    function arbChainID() external view returns (uint);

    /**
     * @notice Get Arbitrum block number (distinct from L1 block number; Arbitrum genesis block has block number 0)
     * @return block number as int
     */
    function arbBlockNumber() external view returns (uint);

    /**
     * @notice Send given amount of Eth to dest from sender.
     * This is a convenience function, which is equivalent to calling sendTxToL1 with empty calldataForL1.
     * @param destination recipient address on L1
     * @return unique identifier for this L2-to-L1 transaction.
     */
    function withdrawEth(address destination) external payable returns (uint);

    /**
     * @notice Send a transaction to L1
     * @param destination recipient address on L1
     * @param calldataForL1 (optional) calldata for L1 contract call
     * @return a unique identifier for this L2-to-L1 transaction.
     */
    function sendTxToL1(address destination, bytes calldata calldataForL1) external payable returns (uint);

    /**
     * @notice get the number of transactions issued by the given external account or the account sequence number of the given contract
     * @param account target account
     * @return the number of transactions issued by the given external account or the account sequence number of the given contract
     */
    function getTransactionCount(address account) external view returns (uint256);

    /**
     * @notice get the value of target L2 storage slot
     * This function is only callable from address 0 to prevent contracts from being able to call it
     * @param account target account
     * @param index target index of storage slot
     * @return stotage value for the given account at the given index
     */
    function getStorageAt(address account, uint256 index) external view returns (uint256);

    /**
     * @notice check if current call is coming from l1
     * @return true if the caller of this was called directly from L1
     */
    function isTopLevelCall() external view returns (bool);

    /**
     * @notice check if the caller (of this caller of this) is an aliased L1 contract address
     * @return true iff the caller's address is an alias for an L1 contract address
     */
    function wasMyCallersAddressAliased() external view returns (bool);

    /**
     * @notice return the address of the caller (of this caller of this), without applying L1 contract address aliasing
     * @return address of the caller's caller, without applying L1 contract address aliasing
     */
    function myCallersAddressWithoutAliasing() external view returns (address);

    /**
     * @notice map L1 sender contract address to its L2 alias
     * @param sender sender address
     * @param dest destination address
     * @return aliased sender address
     */
    function mapL1SenderContractAddressToL2Alias(address sender, address dest) external pure returns (address);

    /**
     * @notice get the caller's amount of available storage gas
     * @return amount of storage gas available to the caller
     */
    function getStorageGasAvailable() external view returns (uint);

    event L2ToL1Transaction(
        address caller,
        address indexed destination,
        uint indexed uniqueId,
        uint indexed batchNumber,
        uint indexInBatch,
        uint arbBlockNum,
        uint ethBlockNum,
        uint timestamp,
        uint callvalue,
        bytes data
    );
}

