contracts/contracts/GFarmTradingStorageV.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../interfaces/TokenInterfaceV5.sol";
import "../interfaces/AggregatorInterfaceV5.sol";
import "../interfaces/PausableInterfaceV5.sol";
import "../interfaces/NftInterfaceV5.sol";

contract GFarmTradingStorageV5 {
    // Constants
    uint public constant PRECISION = 1e10;
    bytes32 public constant MINTER_ROLE =
        0x9f2df0fed2c77648de5860a4cc508cd0818c85b8b8a1ab4ceeef8d981c8956a6;
    TokenInterfaceV5 public constant dai =
        TokenInterfaceV5(0x0d9080e677505d29Be4Da1DDd855c6b523F1D38d);
    TokenInterfaceV5 public constant linkErc677 =
        TokenInterfaceV5(0x779877A7B0D9E8603169DdbD7836e478b4624789);

    // Contracts (updatable)
    AggregatorInterfaceV5 public priceAggregator;
    PausableInterfaceV5 public trading;
    PausableInterfaceV5 public callbacks;
    TokenInterfaceV5 public token =
        TokenInterfaceV5(0x20F16AAd14b69FB7aeab936060bc71De2B202a31);
    // NftInterfaceV5[5] public nfts = [
    //     NftInterfaceV5(0xF9A4c522E327935BD1F5a338c121E14e4cc1f898),
    //     NftInterfaceV5(0x77cd42B925e1A82f41d852D6BE727CFc88fddBbC),
    //     NftInterfaceV5(0x3378AD81D09DE23725Ee9B9270635c97Ed601921),
    //     NftInterfaceV5(0x02e2c5825C1a3b69C0417706DbE1327C2Af3e6C2),
    //     NftInterfaceV5(0x2D266A94469d05C9e06D52A4D0d9C23b157767c2)
    // ];
    address public vault;
    address public tokenDaiRouter;

    // Trading variables
    uint public maxTradesPerPair = 3;
    uint public maxTradesPerBlock = 5;
    uint public maxPendingMarketOrders = 5;
    uint public maxGainP = 900; // %
    uint public maxSlP = 80; // %
    uint public defaultLeverageUnlocked = 50; // x
    uint public nftSuccessTimelock = 50; // 50 blocks
    uint[5] public spreadReductionsP = [15, 20, 25, 30, 35]; // %

    // Gov & dev addresses (updatable)
    address public gov = 0xd7D1dCba2c678ee7e049BD55176354E7C5bBdcCA;
    address public dev = 0x1879449eCAc0661FF450723f96498c109062E0dD;

    // Gov & dev fees
    uint public devFeesToken; // 1e18
    uint public devFeesDai; // 1e18
    uint public govFeesToken; // 1e18
    uint public govFeesDai; // 1e18

    // Stats
    uint public tokensBurned; // 1e18
    uint public tokensMinted; // 1e18
    uint public nftRewards; // 1e18

    // Enums
    enum LimitOrder {
        TP,
        SL,
        LIQ,
        OPEN
    }

    // Structs
    struct Trader {
        uint leverageUnlocked;
        address referral;
        uint referralRewardsTotal; // 1e18
    }
    struct Trade {
        address trader;
        uint pairIndex;
        uint index;
        // uint initialPosToken; // 1e18
        uint positionSizeDai; // 1e18
        uint openPrice; // PRECISION
        bool buy;
        uint leverage;
        uint tp; // PRECISION
        uint sl; // PRECISION
    }
    struct TradeInfo {
        uint tokenId;
        // uint tokenPriceDai; // PRECISION
        uint openInterestDai; // 1e18
        uint tpLastUpdated;
        uint slLastUpdated;
        bool beingMarketClosed;
    }
    struct OpenLimitOrder {
        address trader;
        uint pairIndex;
        uint index;
        uint positionSize; // 1e18 (DAI or GFARM2)
        uint spreadReductionP;
        bool buy;
        uint leverage;
        uint tp; // PRECISION (%)
        uint sl; // PRECISION (%)
        uint minPrice; // PRECISION
        uint maxPrice; // PRECISION
        uint block;
        uint tokenId; // index in supportedTokens
    }
    struct PendingMarketOrder {
        Trade trade;
        uint block;
        uint wantedPrice; // PRECISION
        uint slippageP; // PRECISION (%)
        uint spreadReductionP;
        uint tokenId; // index in supportedTokens
    }
    struct PendingNftOrder {
        address nftHolder;
        uint nftId;
        address trader;
        uint pairIndex;
        uint index;
        LimitOrder orderType;
    }

    // Supported tokens to open trades with
    address[] public supportedTokens;

    // User info mapping
    mapping(address => Trader) public traders;

    // Trades mappings
    mapping(address => mapping(uint => mapping(uint => Trade)))
        public openTrades;
    mapping(address => mapping(uint => mapping(uint => TradeInfo)))
        public openTradesInfo;
    mapping(address => mapping(uint => uint)) public openTradesCount;

    // Limit orders mappings
    mapping(address => mapping(uint => mapping(uint => uint)))
        public openLimitOrderIds;
    mapping(address => mapping(uint => uint)) public openLimitOrdersCount;
    OpenLimitOrder[] public openLimitOrders;

    // Pending orders mappings
    mapping(uint => PendingMarketOrder) public reqID_pendingMarketOrder;
    mapping(uint => PendingNftOrder) public reqID_pendingNftOrder;
    mapping(address => uint[]) public pendingOrderIds;
    mapping(address => mapping(uint => uint)) public pendingMarketOpenCount;
    mapping(address => mapping(uint => uint)) public pendingMarketCloseCount;

    // List of open trades & limit orders
    mapping(uint => address[]) public pairTraders;
    mapping(address => mapping(uint => uint)) public pairTradersId;

    // Current and max open interests for each pair
    mapping(uint => uint[3]) public openInterestDai; // 1e18 [long,short,max]

    // Restrictions & Timelocks
    mapping(uint => uint) public tradesPerBlock;
    mapping(uint => uint) public nftLastSuccess;

    // List of allowed contracts => can update storage + mint/burn tokens
    mapping(address => bool) public isTradingContract;

    // Events
    event SupportedTokenAdded(address a);
    event TradingContractAdded(address a);
    event TradingContractRemoved(address a);
    event AddressUpdated(string name, address a);
    // event NftsUpdated(NftInterfaceV5[5] nfts);
    event NumberUpdated(string name, uint value);
    event NumberUpdatedPair(string name, uint pairIndex, uint value);
    event SpreadReductionsUpdated(uint[5]);

    constructor() {}

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == gov);
        _;
    }
    modifier onlyTrading() {
        require(
            isTradingContract[msg.sender] &&
                token.hasRole(MINTER_ROLE, msg.sender)
        );
        _;
    }

    // Manage addresses
    function setGov(address _gov) external onlyGov {
        require(_gov != address(0));
        gov = _gov;
        emit AddressUpdated("gov", _gov);
    }

    function setDev(address _dev) external onlyGov {
        require(_dev != address(0));
        dev = _dev;
        emit AddressUpdated("dev", _dev);
    }

    function updateToken(TokenInterfaceV5 _newToken) external onlyGov {
        require(trading.isPaused() && callbacks.isPaused(), "NOT_PAUSED");
        require(address(_newToken) != address(0));
        token = _newToken;
        emit AddressUpdated("token", address(_newToken));
    }

    // function updateNfts(NftInterfaceV5[5] memory _nfts) external onlyGov{
    //     require(address(_nfts[0]) != address(0));
    //     nfts = _nfts;
    //     emit NftsUpdated(_nfts);
    // }
    // Trading + callbacks contracts
    function addTradingContract(address _trading) external onlyGov {
        require(token.hasRole(MINTER_ROLE, _trading), "NOT_MINTER");
        require(_trading != address(0));
        isTradingContract[_trading] = true;
        emit TradingContractAdded(_trading);
    }

    function removeTradingContract(address _trading) external onlyGov {
        require(_trading != address(0));
        isTradingContract[_trading] = false;
        emit TradingContractRemoved(_trading);
    }

    function addSupportedToken(address _token) external onlyGov {
        require(_token != address(0));
        supportedTokens.push(_token);
        emit SupportedTokenAdded(_token);
    }

    function setPriceAggregator(address _aggregator) external onlyGov {
        require(_aggregator != address(0));
        priceAggregator = AggregatorInterfaceV5(_aggregator);
        emit AddressUpdated("priceAggregator", _aggregator);
    }

    function setVault(address _vault) external onlyGov {
        require(_vault != address(0));
        vault = _vault;
        emit AddressUpdated("vault", _vault);
    }

    function setTrading(address _trading) external onlyGov {
        require(_trading != address(0));
        trading = PausableInterfaceV5(_trading);
        emit AddressUpdated("trading", _trading);
    }

    function setCallbacks(address _callbacks) external onlyGov {
        require(_callbacks != address(0));
        callbacks = PausableInterfaceV5(_callbacks);
        emit AddressUpdated("callbacks", _callbacks);
    }

    function setTokenDaiRouter(address _tokenDaiRouter) external onlyGov {
        require(_tokenDaiRouter != address(0));
        tokenDaiRouter = _tokenDaiRouter;
        emit AddressUpdated("tokenDaiRouter", _tokenDaiRouter);
    }

    // Manage trading variables
    function setMaxTradesPerBlock(uint _maxTradesPerBlock) external onlyGov {
        require(_maxTradesPerBlock > 0);
        maxTradesPerBlock = _maxTradesPerBlock;
        emit NumberUpdated("maxTradesPerBlock", _maxTradesPerBlock);
    }

    function setMaxTradesPerPair(uint _maxTradesPerPair) external onlyGov {
        require(_maxTradesPerPair > 0);
        maxTradesPerPair = _maxTradesPerPair;
        emit NumberUpdated("maxTradesPerPair", _maxTradesPerPair);
    }

    function setMaxPendingMarketOrders(
        uint _maxPendingMarketOrders
    ) external onlyGov {
        require(_maxPendingMarketOrders > 0);
        maxPendingMarketOrders = _maxPendingMarketOrders;
        emit NumberUpdated("maxPendingMarketOrders", _maxPendingMarketOrders);
    }

    function setMaxGainP(uint _max) external onlyGov {
        require(_max >= 300);
        maxGainP = _max;
        emit NumberUpdated("maxGainP", _max);
    }

    function setDefaultLeverageUnlocked(uint _lev) external onlyGov {
        require(_lev > 0);
        defaultLeverageUnlocked = _lev;
        emit NumberUpdated("defaultLeverageUnlocked", _lev);
    }

    function setMaxSlP(uint _max) external onlyGov {
        require(_max >= 50);
        maxSlP = _max;
        emit NumberUpdated("maxSlP", _max);
    }

    function setNftSuccessTimelock(uint _blocks) external onlyGov {
        nftSuccessTimelock = _blocks;
        emit NumberUpdated("nftSuccessTimelock", _blocks);
    }

    function setSpreadReductionsP(uint[5] calldata _r) external onlyGov {
        require(
            _r[0] > 0 &&
                _r[1] > _r[0] &&
                _r[2] > _r[1] &&
                _r[3] > _r[2] &&
                _r[4] > _r[3]
        );
        spreadReductionsP = _r;
        emit SpreadReductionsUpdated(_r);
    }

    function setMaxOpenInterestDai(
        uint _pairIndex,
        uint _newMaxOpenInterest
    ) external onlyGov {
        // Can set max open interest to 0 to pause trading on this pair only
        openInterestDai[_pairIndex][2] = _newMaxOpenInterest;
        emit NumberUpdatedPair(
            "maxOpenInterestDai",
            _pairIndex,
            _newMaxOpenInterest
        );
    }

    // Manage stored trades
    function storeTrade(
        Trade memory _trade,
        TradeInfo memory _tradeInfo
    ) external onlyTrading {
        _trade.index = firstEmptyTradeIndex(_trade.trader, _trade.pairIndex);
        openTrades[_trade.trader][_trade.pairIndex][_trade.index] = _trade;

        openTradesCount[_trade.trader][_trade.pairIndex]++;
        tradesPerBlock[block.number]++;

        if (openTradesCount[_trade.trader][_trade.pairIndex] == 1) {
            pairTradersId[_trade.trader][_trade.pairIndex] = pairTraders[
                _trade.pairIndex
            ].length;
            pairTraders[_trade.pairIndex].push(_trade.trader);
        }

        _tradeInfo.beingMarketClosed = false;
        openTradesInfo[_trade.trader][_trade.pairIndex][
            _trade.index
        ] = _tradeInfo;

        updateOpenInterestDai(
            _trade.pairIndex,
            _tradeInfo.openInterestDai,
            true,
            _trade.buy
        );
    }

    function unregisterTrade(
        address trader,
        uint pairIndex,
        uint index
    ) external onlyTrading {
        Trade storage t = openTrades[trader][pairIndex][index];
        TradeInfo storage i = openTradesInfo[trader][pairIndex][index];
        if (t.leverage == 0) {
            return;
        }

        updateOpenInterestDai(pairIndex, i.openInterestDai, false, t.buy);

        if (openTradesCount[trader][pairIndex] == 1) {
            uint _pairTradersId = pairTradersId[trader][pairIndex];
            address[] storage p = pairTraders[pairIndex];

            p[_pairTradersId] = p[p.length - 1];
            pairTradersId[p[_pairTradersId]][pairIndex] = _pairTradersId;

            delete pairTradersId[trader][pairIndex];
            p.pop();
        }

        delete openTrades[trader][pairIndex][index];
        delete openTradesInfo[trader][pairIndex][index];

        openTradesCount[trader][pairIndex]--;
        tradesPerBlock[block.number]++;
    }

    // Manage pending market orders
    function storePendingMarketOrder(
        PendingMarketOrder memory _order,
        uint _id,
        bool _open
    ) external onlyTrading {
        pendingOrderIds[_order.trade.trader].push(_id);

        reqID_pendingMarketOrder[_id] = _order;
        reqID_pendingMarketOrder[_id].block = block.number;

        if (_open) {
            pendingMarketOpenCount[_order.trade.trader][
                _order.trade.pairIndex
            ]++;
        } else {
            pendingMarketCloseCount[_order.trade.trader][
                _order.trade.pairIndex
            ]++;
            openTradesInfo[_order.trade.trader][_order.trade.pairIndex][
                _order.trade.index
            ].beingMarketClosed = true;
        }
    }

    function unregisterPendingMarketOrder(
        uint _id,
        bool _open
    ) external onlyTrading {
        PendingMarketOrder memory _order = reqID_pendingMarketOrder[_id];
        uint[] storage orderIds = pendingOrderIds[_order.trade.trader];

        for (uint i = 0; i < orderIds.length; i++) {
            if (orderIds[i] == _id) {
                if (_open) {
                    pendingMarketOpenCount[_order.trade.trader][
                        _order.trade.pairIndex
                    ]--;
                } else {
                    pendingMarketCloseCount[_order.trade.trader][
                        _order.trade.pairIndex
                    ]--;
                    openTradesInfo[_order.trade.trader][_order.trade.pairIndex][
                        _order.trade.index
                    ].beingMarketClosed = false;
                }

                orderIds[i] = orderIds[orderIds.length - 1];
                orderIds.pop();

                delete reqID_pendingMarketOrder[_id];
                return;
            }
        }
    }

    // Manage open interest
    function updateOpenInterestDai(
        uint _pairIndex,
        uint _leveragedPosDai,
        bool _open,
        bool _long
    ) private {
        uint index = _long ? 0 : 1;
        uint[3] storage o = openInterestDai[_pairIndex];
        o[index] = _open
            ? o[index] + _leveragedPosDai
            : o[index] - _leveragedPosDai;
    }

    // Manage open limit orders
    function storeOpenLimitOrder(OpenLimitOrder memory o) external onlyTrading {
        o.index = firstEmptyOpenLimitIndex(o.trader, o.pairIndex);
        o.block = block.number;
        openLimitOrders.push(o);
        openLimitOrderIds[o.trader][o.pairIndex][o.index] =
            openLimitOrders.length -
            1;
        openLimitOrdersCount[o.trader][o.pairIndex]++;
    }

    function updateOpenLimitOrder(
        OpenLimitOrder calldata _o
    ) external onlyTrading {
        if (!hasOpenLimitOrder(_o.trader, _o.pairIndex, _o.index)) {
            return;
        }
        OpenLimitOrder storage o = openLimitOrders[
            openLimitOrderIds[_o.trader][_o.pairIndex][_o.index]
        ];
        o.positionSize = _o.positionSize;
        o.buy = _o.buy;
        o.leverage = _o.leverage;
        o.tp = _o.tp;
        o.sl = _o.sl;
        o.minPrice = _o.minPrice;
        o.maxPrice = _o.maxPrice;
        o.block = block.number;
    }

    function unregisterOpenLimitOrder(
        address _trader,
        uint _pairIndex,
        uint _index
    ) external onlyTrading {
        if (!hasOpenLimitOrder(_trader, _pairIndex, _index)) {
            return;
        }

        // Copy last order to deleted order => update id of this limit order
        uint id = openLimitOrderIds[_trader][_pairIndex][_index];
        openLimitOrders[id] = openLimitOrders[openLimitOrders.length - 1];
        openLimitOrderIds[openLimitOrders[id].trader][
            openLimitOrders[id].pairIndex
        ][openLimitOrders[id].index] = id;

        // Remove
        delete openLimitOrderIds[_trader][_pairIndex][_index];
        openLimitOrders.pop();

        openLimitOrdersCount[_trader][_pairIndex]--;
    }

    // Manage NFT orders
    function storePendingNftOrder(
        PendingNftOrder memory _nftOrder,
        uint _orderId
    ) external onlyTrading {
        reqID_pendingNftOrder[_orderId] = _nftOrder;
    }

    function unregisterPendingNftOrder(uint _order) external onlyTrading {
        delete reqID_pendingNftOrder[_order];
    }

    // Manage open trade
    function updateSl(
        address _trader,
        uint _pairIndex,
        uint _index,
        uint _newSl
    ) external onlyTrading {
        Trade storage t = openTrades[_trader][_pairIndex][_index];
        TradeInfo storage i = openTradesInfo[_trader][_pairIndex][_index];
        if (t.leverage == 0) {
            return;
        }
        t.sl = _newSl;
        i.slLastUpdated = block.number;
    }

    function updateTp(
        address _trader,
        uint _pairIndex,
        uint _index,
        uint _newTp
    ) external onlyTrading {
        Trade storage t = openTrades[_trader][_pairIndex][_index];
        TradeInfo storage i = openTradesInfo[_trader][_pairIndex][_index];
        if (t.leverage == 0) {
            return;
        }
        t.tp = _newTp;
        i.tpLastUpdated = block.number;
    }

    function updateTrade(Trade memory _t) external onlyTrading {
        // useful when partial adding/closing
        Trade storage t = openTrades[_t.trader][_t.pairIndex][_t.index];
        if (t.leverage == 0) {
            return;
        }
        // t.initialPosToken = _t.initialPosToken;
        t.positionSizeDai = _t.positionSizeDai;
        t.openPrice = _t.openPrice;
        t.leverage = _t.leverage;
    }

    // Manage referrals
    function storeReferral(
        address _trader,
        address _referral
    ) external onlyTrading {
        Trader storage trader = traders[_trader];
        trader.referral = _referral != address(0) &&
            trader.referral == address(0) &&
            _referral != _trader
            ? _referral
            : trader.referral;
    }

    function increaseReferralRewards(
        address _referral,
        uint _amount
    ) external onlyTrading {
        traders[_referral].referralRewardsTotal += _amount;
    }

    // Manage rewards
    function increaseNftRewards(
        uint _nftId,
        uint _amount
    ) external onlyTrading {
        nftLastSuccess[_nftId] = block.number;
        nftRewards += _amount;
    }

    // Unlock next leverage
    function setLeverageUnlocked(
        address _trader,
        uint _newLeverage
    ) external onlyTrading {
        traders[_trader].leverageUnlocked = _newLeverage;
    }

    // Manage dev & gov fees
    function handleDevGovFees(
        uint _pairIndex,
        uint _leveragedPositionSize,
        bool _dai,
        bool _fullFee
    ) external onlyTrading returns (uint fee) {
        fee =
            (_leveragedPositionSize * priceAggregator.openFeeP(_pairIndex)) /
            PRECISION /
            100;
        if (!_fullFee) {
            fee /= 2;
        }

        if (_dai) {
            govFeesDai += fee;
            devFeesDai += fee;
        } else {
            govFeesToken += fee;
            devFeesToken += fee;
        }

        fee *= 2;
    }

    function claimFees() external onlyGov {
        token.mint(dev, devFeesToken);
        token.mint(gov, govFeesToken);

        tokensMinted += devFeesToken + govFeesToken;

        dai.transfer(gov, govFeesDai);
        dai.transfer(dev, devFeesDai);

        devFeesToken = 0;
        govFeesToken = 0;
        devFeesDai = 0;
        govFeesDai = 0;
    }

    // Manage tokens
    function handleTokens(
        address _a,
        uint _amount,
        bool _mint
    ) external onlyTrading {
        if (_mint) {
            token.mint(_a, _amount);
            tokensMinted += _amount;
        } else {
            token.burn(_a, _amount);
            tokensBurned += _amount;
        }
    }

    function transferDai(
        address _from,
        address _to,
        uint _amount
    ) external onlyTrading {
        if (_from == address(this)) {
            dai.transfer(_to, _amount);
        } else {
            dai.transferFrom(_from, _to, _amount);
        }
    }

    function transferLinkToAggregator(
        address _from,
        uint _pairIndex,
        uint _leveragedPosDai
    ) external onlyTrading {
        linkErc677.transferFrom(
            _from,
            address(priceAggregator),
            priceAggregator.linkFee(_pairIndex, _leveragedPosDai)
        );
    }

    // View utils functions
    function firstEmptyTradeIndex(
        address trader,
        uint pairIndex
    ) public view returns (uint index) {
        for (uint i = 0; i < maxTradesPerPair; i++) {
            if (openTrades[trader][pairIndex][i].leverage == 0) {
                index = i;
                break;
            }
        }
    }

    function firstEmptyOpenLimitIndex(
        address trader,
        uint pairIndex
    ) public view returns (uint index) {
        for (uint i = 0; i < maxTradesPerPair; i++) {
            if (!hasOpenLimitOrder(trader, pairIndex, i)) {
                index = i;
                break;
            }
        }
    }

    function hasOpenLimitOrder(
        address trader,
        uint pairIndex,
        uint index
    ) public view returns (bool) {
        if (openLimitOrders.length == 0) {
            return false;
        }
        OpenLimitOrder storage o = openLimitOrders[
            openLimitOrderIds[trader][pairIndex][index]
        ];
        return
            o.trader == trader && o.pairIndex == pairIndex && o.index == index;
    }

    // Additional getters
    function getReferral(address _trader) external view returns (address) {
        return traders[_trader].referral;
    }

    function getLeverageUnlocked(address _trader) external view returns (uint) {
        return traders[_trader].leverageUnlocked;
    }

    function pairTradersArray(
        uint _pairIndex
    ) external view returns (address[] memory) {
        return pairTraders[_pairIndex];
    }

    function getPendingOrderIds(
        address _trader
    ) external view returns (uint[] memory) {
        return pendingOrderIds[_trader];
    }

    function pendingOrderIdsCount(
        address _trader
    ) external view returns (uint) {
        return pendingOrderIds[_trader].length;
    }

    function getOpenLimitOrder(
        address _trader,
        uint _pairIndex,
        uint _index
    ) external view returns (OpenLimitOrder memory) {
        require(hasOpenLimitOrder(_trader, _pairIndex, _index), "23");
        return openLimitOrders[openLimitOrderIds[_trader][_pairIndex][_index]];
    }

    function getOpenLimitOrders()
        external
        view
        returns (OpenLimitOrder[] memory)
    {
        return openLimitOrders;
    }

    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokens;
    }

    function getSpreadReductionsArray() external view returns (uint[5] memory) {
        return spreadReductionsP;
    }
}

contracts/contracts/GNSBorrowingFeesV6_4.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../libraries/Initializable.sol";

import "../interfaces/GNSBorrowingFeesInterfaceV6_4.sol";
import "../interfaces/StorageInterfaceV5.sol";
import "../interfaces/GNSPairInfosInterfaceV6.sol";
import "../libraries/ChainUtils.sol";

contract GNSBorrowingFeesV6_4 is Initializable, GNSBorrowingFeesInterfaceV6_4 {
    // Constants
    uint constant P_1 = 1e10;
    uint constant P_2 = 1e40;

    // Addresses
    StorageInterfaceV5 public storageT;
    GNSPairInfosInterfaceV6 public pairInfos;

    // State
    mapping(uint16 => Group) public groups;
    mapping(uint => Pair) public pairs;
    mapping(address => mapping(uint => mapping(uint => InitialAccFees)))
        public initialAccFees;
    mapping(uint => PairOi) public pairOis;
    mapping(uint => uint48) public groupFeeExponents;

    function initialize(
        StorageInterfaceV5 _storageT,
        GNSPairInfosInterfaceV6 _pairInfos
    ) external initializer {
        require(
            address(_storageT) != address(0) &&
                address(_pairInfos) != address(0),
            "WRONG_PARAMS"
        );

        storageT = _storageT;
        pairInfos = _pairInfos;
    }

    // Modifiers
    modifier onlyManager() {
        require(msg.sender == pairInfos.manager(), "MANAGER_ONLY");
        _;
    }

    modifier onlyCallbacks() {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");
        _;
    }

    // Manage pair params
    function setPairParams(
        uint pairIndex,
        PairParams calldata value
    ) external onlyManager {
        _setPairParams(pairIndex, value);
    }

    function setPairParamsArray(
        uint[] calldata indices,
        PairParams[] calldata values
    ) external onlyManager {
        uint len = indices.length;
        require(len == values.length, "WRONG_LENGTH");

        for (uint i; i < len; ) {
            _setPairParams(indices[i], values[i]);
            unchecked {
                ++i;
            }
        }
    }

    function _setPairParams(uint pairIndex, PairParams calldata value) private {
        require(
            value.feeExponent >= 1 && value.feeExponent <= 3,
            "WRONG_EXPONENT"
        );

        Pair storage p = pairs[pairIndex];

        uint16 prevGroupIndex = getPairGroupIndex(pairIndex);
        uint currentBlock = ChainUtils.getBlockNumber();

        _setPairPendingAccFees(pairIndex, currentBlock);

        if (value.groupIndex != prevGroupIndex) {
            _setGroupPendingAccFees(prevGroupIndex, currentBlock);
            _setGroupPendingAccFees(value.groupIndex, currentBlock);

            (uint oiLong, uint oiShort) = getPairOpenInterestDai(pairIndex);

            // Only remove OI from old group if old group is not 0
            _setGroupOi(prevGroupIndex, true, false, oiLong);
            _setGroupOi(prevGroupIndex, false, false, oiShort);

            // Add OI to new group if it's not group 0 (even if old group is 0)
            // So when we assign a pair to a group, it takes into account its OI
            // And group 0 OI will always be 0 but it doesn't matter since it's not used
            _setGroupOi(value.groupIndex, true, true, oiLong);
            _setGroupOi(value.groupIndex, false, true, oiShort);

            Group memory newGroup = groups[value.groupIndex];
            Group memory prevGroup = groups[prevGroupIndex];

            p.groups.push(
                PairGroup(
                    value.groupIndex,
                    ChainUtils.getUint48BlockNumber(currentBlock),
                    newGroup.accFeeLong,
                    newGroup.accFeeShort,
                    prevGroup.accFeeLong,
                    prevGroup.accFeeShort,
                    p.accFeeLong,
                    p.accFeeShort,
                    0 // placeholder
                )
            );

            emit PairGroupUpdated(pairIndex, prevGroupIndex, value.groupIndex);
        }

        p.feePerBlock = value.feePerBlock;
        p.feeExponent = value.feeExponent;
        pairOis[pairIndex].max = value.maxOi;

        emit PairParamsUpdated(
            pairIndex,
            value.groupIndex,
            value.feePerBlock,
            value.feeExponent,
            value.maxOi
        );
    }

    // Manage group params
    function setGroupParams(
        uint16 groupIndex,
        GroupParams calldata value
    ) external onlyManager {
        _setGroupParams(groupIndex, value);
    }

    function setGroupParamsArray(
        uint16[] calldata indices,
        GroupParams[] calldata values
    ) external onlyManager {
        uint len = indices.length;
        require(len == values.length, "WRONG_LENGTH");

        for (uint i; i < len; ) {
            _setGroupParams(indices[i], values[i]);
            unchecked {
                ++i;
            }
        }
    }

    function _setGroupParams(
        uint16 groupIndex,
        GroupParams calldata value
    ) private {
        require(groupIndex > 0, "GROUP_0");
        require(
            value.feeExponent >= 1 && value.feeExponent <= 3,
            "WRONG_EXPONENT"
        );

        _setGroupPendingAccFees(groupIndex, ChainUtils.getBlockNumber());

        Group storage g = groups[groupIndex];
        g.feePerBlock = value.feePerBlock;
        g.maxOi = uint80(value.maxOi);
        groupFeeExponents[groupIndex] = value.feeExponent;

        emit GroupUpdated(
            groupIndex,
            value.feePerBlock,
            value.maxOi,
            value.feeExponent
        );
    }

    // Group OI setter
    function _setGroupOi(
        uint16 groupIndex,
        bool long,
        bool increase,
        uint amount // 1e18
    ) private {
        Group storage group = groups[groupIndex];
        uint112 amountFinal;

        if (groupIndex > 0) {
            amount = (amount * P_1) / 1e18; // 1e10
            require(amount <= type(uint112).max, "OVERFLOW");

            amountFinal = uint112(amount);

            if (long) {
                group.oiLong = increase
                    ? group.oiLong + amountFinal
                    : group.oiLong -
                        (
                            group.oiLong > amountFinal
                                ? amountFinal
                                : group.oiLong
                        );
            } else {
                group.oiShort = increase
                    ? group.oiShort + amountFinal
                    : group.oiShort -
                        (
                            group.oiShort > amountFinal
                                ? amountFinal
                                : group.oiShort
                        );
            }
        }

        emit GroupOiUpdated(
            groupIndex,
            long,
            increase,
            amountFinal,
            group.oiLong,
            group.oiShort
        );
    }

    // Acc fees getters for pairs and groups
    function getPendingAccFees(
        PendingAccFeesInput memory input
    )
        public
        pure
        returns (uint64 newAccFeeLong, uint64 newAccFeeShort, uint64 delta)
    {
        require(input.currentBlock >= input.accLastUpdatedBlock, "BLOCK_ORDER");

        bool moreShorts = input.oiLong < input.oiShort;
        uint netOi = moreShorts
            ? input.oiShort - input.oiLong
            : input.oiLong - input.oiShort;

        uint _delta = input.maxOi > 0 && input.feeExponent > 0
            ? ((input.currentBlock - input.accLastUpdatedBlock) *
                input.feePerBlock *
                ((netOi * 1e10) / input.maxOi) ** input.feeExponent) /
                (1e18 ** input.feeExponent)
            : 0; // 1e10 (%)

        require(_delta <= type(uint64).max, "OVERFLOW");
        delta = uint64(_delta);

        newAccFeeLong = moreShorts
            ? input.accFeeLong
            : input.accFeeLong + delta;
        newAccFeeShort = moreShorts
            ? input.accFeeShort + delta
            : input.accFeeShort;
    }

    function getPairGroupAccFeesDeltas(
        uint i,
        PairGroup[] memory pairGroups,
        InitialAccFees memory initialFees,
        uint pairIndex,
        bool long,
        uint currentBlock
    )
        public
        view
        returns (uint64 deltaGroup, uint64 deltaPair, bool beforeTradeOpen)
    {
        PairGroup memory group = pairGroups[i];

        beforeTradeOpen = group.block < initialFees.block;

        if (i == pairGroups.length - 1) {
            // Last active group
            deltaGroup = getGroupPendingAccFee(
                group.groupIndex,
                currentBlock,
                long
            );
            deltaPair = getPairPendingAccFee(pairIndex, currentBlock, long);
        } else {
            // Previous groups
            PairGroup memory nextGroup = pairGroups[i + 1];

            // If it's not the first group to be before the trade was opened then fee is 0
            if (beforeTradeOpen && nextGroup.block <= initialFees.block) {
                return (0, 0, beforeTradeOpen);
            }

            deltaGroup = long
                ? nextGroup.prevGroupAccFeeLong
                : nextGroup.prevGroupAccFeeShort;
            deltaPair = long
                ? nextGroup.pairAccFeeLong
                : nextGroup.pairAccFeeShort;
        }

        if (beforeTradeOpen) {
            deltaGroup -= initialFees.accGroupFee;
            deltaPair -= initialFees.accPairFee;
        } else {
            deltaGroup -= (
                long ? group.initialAccFeeLong : group.initialAccFeeShort
            );
            deltaPair -= (long ? group.pairAccFeeLong : group.pairAccFeeShort);
        }
    }

    // Pair acc fees helpers
    function getPairPendingAccFees(
        uint pairIndex,
        uint currentBlock
    )
        public
        view
        returns (uint64 accFeeLong, uint64 accFeeShort, uint64 pairAccFeeDelta)
    {
        Pair memory pair = pairs[pairIndex];

        (uint pairOiLong, uint pairOiShort) = getPairOpenInterestDai(pairIndex);

        (accFeeLong, accFeeShort, pairAccFeeDelta) = getPendingAccFees(
            PendingAccFeesInput(
                pair.accFeeLong,
                pair.accFeeShort,
                pairOiLong,
                pairOiShort,
                pair.feePerBlock,
                currentBlock,
                pair.accLastUpdatedBlock,
                pairOis[pairIndex].max,
                pair.feeExponent
            )
        );
    }

    function getPairPendingAccFee(
        uint pairIndex,
        uint currentBlock,
        bool long
    ) public view returns (uint64 accFee) {
        (uint64 accFeeLong, uint64 accFeeShort, ) = getPairPendingAccFees(
            pairIndex,
            currentBlock
        );
        return long ? accFeeLong : accFeeShort;
    }

    function _setPairPendingAccFees(
        uint pairIndex,
        uint currentBlock
    ) private returns (uint64 accFeeLong, uint64 accFeeShort) {
        (accFeeLong, accFeeShort, ) = getPairPendingAccFees(
            pairIndex,
            currentBlock
        );

        Pair storage pair = pairs[pairIndex];

        (pair.accFeeLong, pair.accFeeShort) = (accFeeLong, accFeeShort);
        pair.accLastUpdatedBlock = ChainUtils.getUint48BlockNumber(
            currentBlock
        );

        emit PairAccFeesUpdated(
            pairIndex,
            currentBlock,
            pair.accFeeLong,
            pair.accFeeShort
        );
    }

    // Group acc fees helpers
    function getGroupPendingAccFees(
        uint16 groupIndex,
        uint currentBlock
    )
        public
        view
        returns (uint64 accFeeLong, uint64 accFeeShort, uint64 groupAccFeeDelta)
    {
        Group memory group = groups[groupIndex];

        (accFeeLong, accFeeShort, groupAccFeeDelta) = getPendingAccFees(
            PendingAccFeesInput(
                group.accFeeLong,
                group.accFeeShort,
                (uint(group.oiLong) * 1e18) / P_1,
                (uint(group.oiShort) * 1e18) / P_1,
                group.feePerBlock,
                currentBlock,
                group.accLastUpdatedBlock,
                uint72(group.maxOi),
                groupFeeExponents[groupIndex]
            )
        );
    }

    function getGroupPendingAccFee(
        uint16 groupIndex,
        uint currentBlock,
        bool long
    ) public view returns (uint64 accFee) {
        (uint64 accFeeLong, uint64 accFeeShort, ) = getGroupPendingAccFees(
            groupIndex,
            currentBlock
        );
        return long ? accFeeLong : accFeeShort;
    }

    function _setGroupPendingAccFees(
        uint16 groupIndex,
        uint currentBlock
    ) private returns (uint64 accFeeLong, uint64 accFeeShort) {
        (accFeeLong, accFeeShort, ) = getGroupPendingAccFees(
            groupIndex,
            currentBlock
        );

        Group storage group = groups[groupIndex];

        (group.accFeeLong, group.accFeeShort) = (accFeeLong, accFeeShort);
        group.accLastUpdatedBlock = ChainUtils.getUint48BlockNumber(
            currentBlock
        );

        emit GroupAccFeesUpdated(
            groupIndex,
            currentBlock,
            group.accFeeLong,
            group.accFeeShort
        );
    }

    // Interaction with callbacks
    function handleTradeAction(
        address trader,
        uint pairIndex,
        uint index,
        uint positionSizeDai, // 1e18 (collateral * leverage)
        bool open,
        bool long
    ) external override {
        uint16 groupIndex = getPairGroupIndex(pairIndex);
        uint currentBlock = ChainUtils.getBlockNumber();

        (
            uint64 pairAccFeeLong,
            uint64 pairAccFeeShort
        ) = _setPairPendingAccFees(pairIndex, currentBlock);
        (
            uint64 groupAccFeeLong,
            uint64 groupAccFeeShort
        ) = _setGroupPendingAccFees(groupIndex, currentBlock);

        _setGroupOi(groupIndex, long, open, positionSizeDai);

        if (open) {
            InitialAccFees memory initialFees = InitialAccFees(
                long ? pairAccFeeLong : pairAccFeeShort,
                long ? groupAccFeeLong : groupAccFeeShort,
                ChainUtils.getUint48BlockNumber(currentBlock),
                0 // placeholder
            );

            initialAccFees[trader][pairIndex][index] = initialFees;

            emit TradeInitialAccFeesStored(
                trader,
                pairIndex,
                index,
                initialFees.accPairFee,
                initialFees.accGroupFee
            );
        }

        emit TradeActionHandled(
            trader,
            pairIndex,
            index,
            open,
            long,
            positionSizeDai
        );
    }

    // Important trade getters
    function getTradeBorrowingFee(
        BorrowingFeeInput memory input
    ) public view override returns (uint fee) {
        InitialAccFees memory initialFees = initialAccFees[input.trader][
            input.pairIndex
        ][input.index];
        PairGroup[] memory pairGroups = pairs[input.pairIndex].groups;

        uint currentBlock = ChainUtils.getBlockNumber();

        PairGroup memory firstPairGroup;
        if (pairGroups.length > 0) {
            firstPairGroup = pairGroups[0];
        }

        // If pair has had no group after trade was opened, initialize with pair borrowing fee
        if (
            pairGroups.length == 0 || firstPairGroup.block > initialFees.block
        ) {
            fee = ((
                pairGroups.length == 0
                    ? getPairPendingAccFee(
                        input.pairIndex,
                        currentBlock,
                        input.long
                    )
                    : (
                        input.long
                            ? firstPairGroup.pairAccFeeLong
                            : firstPairGroup.pairAccFeeShort
                    )
            ) - initialFees.accPairFee);
        }

        // Sum of max(pair fee, group fee) for all groups the pair was in while trade was open
        for (uint i = pairGroups.length; i > 0; ) {
            (
                uint64 deltaGroup,
                uint64 deltaPair,
                bool beforeTradeOpen
            ) = getPairGroupAccFeesDeltas(
                    i - 1,
                    pairGroups,
                    initialFees,
                    input.pairIndex,
                    input.long,
                    currentBlock
                );

            fee += (deltaGroup > deltaPair ? deltaGroup : deltaPair);

            // Exit loop at first group before trade was open
            if (beforeTradeOpen) break;
            unchecked {
                --i;
            }
        }

        fee = (input.collateral * input.leverage * fee) / P_1 / 100; // 1e18 (DAI)
    }

    function getTradeLiquidationPrice(
        LiqPriceInput calldata input
    ) external view override returns (uint) {
        return
            pairInfos.getTradeLiquidationPricePure(
                input.openPrice,
                input.long,
                input.collateral,
                input.leverage,
                pairInfos.getTradeRolloverFee(
                    input.trader,
                    input.pairIndex,
                    input.index,
                    input.collateral
                ) +
                    getTradeBorrowingFee(
                        BorrowingFeeInput(
                            input.trader,
                            input.pairIndex,
                            input.index,
                            input.long,
                            input.collateral,
                            input.leverage
                        )
                    ),
                pairInfos.getTradeFundingFee(
                    input.trader,
                    input.pairIndex,
                    input.index,
                    input.long,
                    input.collateral,
                    input.leverage
                )
            );
    }

    // Public getters
    function getPairOpenInterestDai(
        uint pairIndex
    ) public view returns (uint, uint) {
        return (
            storageT.openInterestDai(pairIndex, 0),
            storageT.openInterestDai(pairIndex, 1)
        );
    }

    function getPairGroupIndex(
        uint pairIndex
    ) public view returns (uint16 groupIndex) {
        PairGroup[] memory pairGroups = pairs[pairIndex].groups;
        return
            pairGroups.length == 0
                ? 0
                : pairGroups[pairGroups.length - 1].groupIndex;
    }

    // External getters
    function withinMaxGroupOi(
        uint pairIndex,
        bool long,
        uint positionSizeDai // 1e18
    ) external view override returns (bool) {
        Group memory g = groups[getPairGroupIndex(pairIndex)];
        return
            (g.maxOi == 0) ||
            ((long ? g.oiLong : g.oiShort) + (positionSizeDai * P_1) / 1e18 <=
                g.maxOi);
    }

    function getGroup(
        uint16 groupIndex
    ) external view returns (Group memory, uint48) {
        return (groups[groupIndex], groupFeeExponents[groupIndex]);
    }

    function getPair(
        uint pairIndex
    ) external view returns (Pair memory, PairOi memory) {
        return (pairs[pairIndex], pairOis[pairIndex]);
    }

    function getAllPairs()
        external
        view
        returns (Pair[] memory, PairOi[] memory)
    {
        uint len = storageT.priceAggregator().pairsStorage().pairsCount();
        Pair[] memory p = new Pair[](len);
        PairOi[] memory pairOi = new PairOi[](len);

        for (uint i; i < len; ) {
            p[i] = pairs[i];
            pairOi[i] = pairOis[i];
            unchecked {
                ++i;
            }
        }

        return (p, pairOi);
    }

    function getGroups(
        uint16[] calldata indices
    ) external view returns (Group[] memory, uint48[] memory) {
        Group[] memory g = new Group[](indices.length);
        uint48[] memory e = new uint48[](indices.length);
        uint len = indices.length;

        for (uint i; i < len; ) {
            g[i] = groups[indices[i]];
            e[i] = groupFeeExponents[indices[i]];
            unchecked {
                ++i;
            }
        }

        return (g, e);
    }

    function getTradeInitialAccFees(
        address trader,
        uint pairIndex,
        uint index
    )
        external
        view
        returns (
            InitialAccFees memory borrowingFees,
            GNSPairInfosInterfaceV6.TradeInitialAccFees memory otherFees
        )
    {
        borrowingFees = initialAccFees[trader][pairIndex][index];
        otherFees = pairInfos.tradeInitialAccFees(trader, pairIndex, index);
    }

    function getPairMaxOi(
        uint pairIndex
    ) external view override returns (uint) {
        return pairOis[pairIndex].max;
    }
}

contracts/contracts/GNSOracleRewardsV6_4.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../libraries/Initializable.sol";

import "../libraries/ChainUtils.sol";
import "../interfaces/StorageInterfaceV5.sol";
import "../interfaces/IGNSOracle.sol";

contract GNSOracleRewardsV6_4_1 is Initializable {
    // Constants
    // uint private constant CHAIN_ID_POLY = 137;
    // uint private constant CHAIN_ID_MUMBAI = 80001;
    // uint private constant CHAIN_ID_ARBI = 42161;

    // address private constant NFT_REWARDS_OLD_POLY = 0x8103C0665A544201BBF606d90845d1B2D8005F1c;
    // address private constant NFT_REWARDS_OLD_MUMBAI = 0xf7Ac400b45Bdd2E098FaCA3642bE4d01071BC73B;
    // address private constant NFT_REWARDS_OLD_ARBI = 0xde5750071CacA8db173FC6543D23d0BCACACFEC3;

    uint private constant MIN_TRIGGER_TIMEOUT = 1;

    // Addresses (constant)
    StorageInterfaceV5 public storageT;
    // mapping(uint => address) public nftRewardsOldByChainId;

    // Params (adjustable)
    uint public triggerTimeout; // blocks
    address[] public oracles; // oracles rewarded

    // Custom data types
    struct TriggeredLimitId {
        address trader;
        uint pairIndex;
        uint index;
        StorageInterfaceV5.LimitOrder order;
    }

    enum OpenLimitOrderType {
        LEGACY,
        REVERSAL,
        MOMENTUM
    }

    // State
    mapping(address => uint) public pendingRewardsGns;
    mapping(address => mapping(uint => mapping(uint => mapping(StorageInterfaceV5.LimitOrder => uint))))
        public triggeredLimits;
    mapping(address => mapping(uint => mapping(uint => OpenLimitOrderType)))
        public openLimitOrderTypes;

    bool public stateCopied;

    // Events
    // event OldLimitTypesCopied(address oldContract, uint start, uint end);
    // event StateCopyDone();
    event TriggerTimeoutUpdated(uint value);
    event OraclesUpdated(uint oraclesCount);

    event TriggeredFirst(TriggeredLimitId id);
    event TriggerUnregistered(TriggeredLimitId id);
    event TriggerRewarded(
        TriggeredLimitId id,
        uint rewardGns,
        uint rewardGnsPerOracle,
        uint oraclesCount
    );
    event RewardsClaimed(address oracle, uint amountGns);
    event OpenLimitOrderTypeSet(
        address trader,
        uint pairIndex,
        uint index,
        OpenLimitOrderType value
    );

    function initialize(
        StorageInterfaceV5 _storageT,
        uint _triggerTimeout,
        uint _oraclesCount
    ) external initializer {
        require(
            address(_storageT) != address(0) &&
                _triggerTimeout >= MIN_TRIGGER_TIMEOUT &&
                _oraclesCount > 0,
            "WRONG_PARAMS"
        );

        // nftRewardsOldByChainId[CHAIN_ID_POLY] = NFT_REWARDS_OLD_POLY;
        // nftRewardsOldByChainId[CHAIN_ID_MUMBAI] = NFT_REWARDS_OLD_MUMBAI;
        // nftRewardsOldByChainId[CHAIN_ID_ARBI] = NFT_REWARDS_OLD_ARBI;

        storageT = _storageT;
        triggerTimeout = _triggerTimeout;

        _updateOracles(_oraclesCount);
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyTrading() {
        require(msg.sender == storageT.trading(), "TRADING_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");
        _;
    }

    // Copy limit order types from old nft rewards contract
    // function copyOldLimitTypes(uint _start, uint _end) external onlyGov {
    //     require(!stateCopied, "COPY_DONE");
    //     require(_start <= _end, "START_AFTER_END");

    //     address oldAddress = nftRewardsOldByChainId[block.chainid];
    //     require(oldAddress != address(0), "UNKNOWN_CHAIN");

    //     StorageInterfaceV5.OpenLimitOrder[] memory openLimitOrders = IStateCopyUtils(address(storageT))
    //         .getOpenLimitOrders();

    //     require(_start < openLimitOrders.length, "START_TOO_BIG");

    //     if (_end >= openLimitOrders.length) {
    //         _end = openLimitOrders.length - 1;
    //     }

    //     NftRewardsInterfaceV6_3_1 old = NftRewardsInterfaceV6_3_1(oldAddress);

    //     for (uint i = _start; i <= _end; ) {
    //         StorageInterfaceV5.OpenLimitOrder memory o = openLimitOrders[i];

    //         openLimitOrderTypes[o.trader][o.pairIndex][o.index] = OpenLimitOrderType(
    //             uint(old.openLimitOrderTypes(o.trader, o.pairIndex, o.index))
    //         );

    //         unchecked {
    //             ++i;
    //         }
    //     }

    //     emit OldLimitTypesCopied(oldAddress, _start, _end);
    // }

    // function setStateCopyAsDone() external onlyGov {
    //     stateCopied = true;

    //     emit StateCopyDone();
    // }

    // Manage params
    function updateTriggerTimeout(uint _triggerTimeout) external onlyGov {
        require(_triggerTimeout >= MIN_TRIGGER_TIMEOUT, "BELOW_MIN");

        triggerTimeout = _triggerTimeout;

        emit TriggerTimeoutUpdated(_triggerTimeout);
    }

    function _updateOracles(uint _oraclesCount) private {
        require(_oraclesCount > 0, "VALUE_ZERO");

        delete oracles;

        for (uint i; i < _oraclesCount; ) {
            require(
                address(storageT.priceAggregator()) != address(0),
                "AGGREGATOR_MISSING"
            );

            oracles.push(storageT.priceAggregator().nodes(i));
            unchecked {
                ++i;
            }
        }

        emit OraclesUpdated(_oraclesCount);
    }

    function updateOracles(uint _oraclesCount) external onlyGov {
        _updateOracles(_oraclesCount);
    }

    // Triggers
    function storeTrigger(TriggeredLimitId calldata _id) external onlyTrading {
        triggeredLimits[_id.trader][_id.pairIndex][_id.index][
            _id.order
        ] = ChainUtils.getBlockNumber();

        emit TriggeredFirst(_id);
    }

    function unregisterTrigger(
        TriggeredLimitId calldata _id
    ) external onlyCallbacks {
        delete triggeredLimits[_id.trader][_id.pairIndex][_id.index][_id.order];

        emit TriggerUnregistered(_id);
    }

    // Distribute oracle rewards
    function distributeOracleReward(
        TriggeredLimitId calldata _id,
        uint _reward
    ) external onlyCallbacks {
        require(triggered(_id), "NOT_TRIGGERED");

        uint oraclesCount = oracles.length;
        uint rewardPerOracle = _reward / oraclesCount;

        for (uint i; i < oraclesCount; ) {
            pendingRewardsGns[oracles[i]] += rewardPerOracle;

            unchecked {
                ++i;
            }
        }

        storageT.handleTokens(address(this), _reward, true);

        emit TriggerRewarded(_id, _reward, rewardPerOracle, oraclesCount);
    }

    // Claim oracle rewards
    function claimRewards(address _oracle) external {
        IGNSOracle _o = IGNSOracle(_oracle);

        // msg.sender must either be the oracle owner or an authorized fulfiller
        require(
            _o.owner() == msg.sender || _o.getAuthorizationStatus(msg.sender),
            "NOT_AUTHORIZED"
        );

        uint amountGns = pendingRewardsGns[_oracle];

        pendingRewardsGns[_oracle] = 0;
        storageT.token().transfer(msg.sender, amountGns);

        emit RewardsClaimed(_oracle, amountGns);
    }

    // Manage open limit order types
    function setOpenLimitOrderType(
        address _trader,
        uint _pairIndex,
        uint _index,
        OpenLimitOrderType _type
    ) external onlyTrading {
        openLimitOrderTypes[_trader][_pairIndex][_index] = _type;

        emit OpenLimitOrderTypeSet(_trader, _pairIndex, _index, _type);
    }

    // Getters
    function triggered(
        TriggeredLimitId calldata _id
    ) public view returns (bool) {
        return
            triggeredLimits[_id.trader][_id.pairIndex][_id.index][_id.order] >
            0;
    }

    function timedOut(
        TriggeredLimitId calldata _id
    ) external view returns (bool) {
        uint triggerBlock = triggeredLimits[_id.trader][_id.pairIndex][
            _id.index
        ][_id.order];

        return
            triggerBlock > 0 &&
            ChainUtils.getBlockNumber() - triggerBlock >= triggerTimeout;
    }

    function getOracles() external view returns (address[] memory) {
        return oracles;
    }
}

contracts/contracts/GNSPairInfosV6_1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../interfaces/StorageInterfaceV5.sol";

contract GNSPairInfosV6_1 {
    // Addresses
    StorageInterfaceV5 immutable storageT;
    address public manager;

    // Constant parameters
    uint constant PRECISION = 1e10; // 10 decimals
    uint constant LIQ_THRESHOLD_P = 90; // -90% (of collateral)

    // Adjustable parameters
    uint public maxNegativePnlOnOpenP = 40 * PRECISION; // PRECISION (%)

    // Pair parameters
    struct PairParams {
        uint onePercentDepthAbove; // DAI
        uint onePercentDepthBelow; // DAI
        uint rolloverFeePerBlockP; // PRECISION (%)
        uint fundingFeePerBlockP; // PRECISION (%)
    }

    mapping(uint => PairParams) public pairParams;

    // Pair acc funding fees
    struct PairFundingFees {
        int accPerOiLong; // 1e18 (DAI)
        int accPerOiShort; // 1e18 (DAI)
        uint lastUpdateBlock;
    }

    mapping(uint => PairFundingFees) public pairFundingFees;

    // Pair acc rollover fees
    struct PairRolloverFees {
        uint accPerCollateral; // 1e18 (DAI)
        uint lastUpdateBlock;
    }

    mapping(uint => PairRolloverFees) public pairRolloverFees;

    // Trade initial acc fees
    struct TradeInitialAccFees {
        uint rollover; // 1e18 (DAI)
        int funding; // 1e18 (DAI)
        bool openedAfterUpdate;
    }

    mapping(address => mapping(uint => mapping(uint => TradeInitialAccFees)))
        public tradeInitialAccFees;

    // Events
    event ManagerUpdated(address value);
    event MaxNegativePnlOnOpenPUpdated(uint value);

    event PairParamsUpdated(uint pairIndex, PairParams value);
    event OnePercentDepthUpdated(
        uint pairIndex,
        uint valueAbove,
        uint valueBelow
    );
    event RolloverFeePerBlockPUpdated(uint pairIndex, uint value);
    event FundingFeePerBlockPUpdated(uint pairIndex, uint value);

    event TradeInitialAccFeesStored(
        address trader,
        uint pairIndex,
        uint index,
        uint rollover,
        int funding
    );

    event AccFundingFeesStored(uint pairIndex, int valueLong, int valueShort);
    event AccRolloverFeesStored(uint pairIndex, uint value);

    event FeesCharged(
        uint pairIndex,
        bool long,
        uint collateral, // 1e18 (DAI)
        uint leverage,
        int percentProfit, // PRECISION (%)
        uint rolloverFees, // 1e18 (DAI)
        int fundingFees // 1e18 (DAI)
    );

    constructor(StorageInterfaceV5 _storageT) {
        storageT = _storageT;
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyManager() {
        require(msg.sender == manager, "MANAGER_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");
        _;
    }

    // Set manager address
    function setManager(address _manager) external onlyGov {
        manager = _manager;

        emit ManagerUpdated(_manager);
    }

    // Set max negative PnL % on trade opening
    function setMaxNegativePnlOnOpenP(uint value) external onlyManager {
        maxNegativePnlOnOpenP = value;

        emit MaxNegativePnlOnOpenPUpdated(value);
    }

    // Set parameters for pair
    function setPairParams(
        uint pairIndex,
        PairParams memory value
    ) public onlyManager {
        storeAccRolloverFees(pairIndex);
        storeAccFundingFees(pairIndex);

        pairParams[pairIndex] = value;

        emit PairParamsUpdated(pairIndex, value);
    }

    function setPairParamsArray(
        uint[] memory indices,
        PairParams[] memory values
    ) external onlyManager {
        require(indices.length == values.length, "WRONG_LENGTH");

        for (uint i = 0; i < indices.length; i++) {
            setPairParams(indices[i], values[i]);
        }
    }

    // Set one percent depth for pair
    function setOnePercentDepth(
        uint pairIndex,
        uint valueAbove,
        uint valueBelow
    ) public onlyManager {
        PairParams storage p = pairParams[pairIndex];

        p.onePercentDepthAbove = valueAbove;
        p.onePercentDepthBelow = valueBelow;

        emit OnePercentDepthUpdated(pairIndex, valueAbove, valueBelow);
    }

    function setOnePercentDepthArray(
        uint[] memory indices,
        uint[] memory valuesAbove,
        uint[] memory valuesBelow
    ) external onlyManager {
        require(
            indices.length == valuesAbove.length &&
                indices.length == valuesBelow.length,
            "WRONG_LENGTH"
        );

        for (uint i = 0; i < indices.length; i++) {
            setOnePercentDepth(indices[i], valuesAbove[i], valuesBelow[i]);
        }
    }

    // Set rollover fee for pair
    function setRolloverFeePerBlockP(
        uint pairIndex,
        uint value
    ) public onlyManager {
        require(value <= 25000000, "TOO_HIGH"); // ≈ 100% per day

        storeAccRolloverFees(pairIndex);

        pairParams[pairIndex].rolloverFeePerBlockP = value;

        emit RolloverFeePerBlockPUpdated(pairIndex, value);
    }

    function setRolloverFeePerBlockPArray(
        uint[] memory indices,
        uint[] memory values
    ) external onlyManager {
        require(indices.length == values.length, "WRONG_LENGTH");

        for (uint i = 0; i < indices.length; i++) {
            setRolloverFeePerBlockP(indices[i], values[i]);
        }
    }

    // Set funding fee for pair
    function setFundingFeePerBlockP(
        uint pairIndex,
        uint value
    ) public onlyManager {
        require(value <= 10000000, "TOO_HIGH"); // ≈ 40% per day

        storeAccFundingFees(pairIndex);

        pairParams[pairIndex].fundingFeePerBlockP = value;

        emit FundingFeePerBlockPUpdated(pairIndex, value);
    }

    function setFundingFeePerBlockPArray(
        uint[] memory indices,
        uint[] memory values
    ) external onlyManager {
        require(indices.length == values.length, "WRONG_LENGTH");

        for (uint i = 0; i < indices.length; i++) {
            setFundingFeePerBlockP(indices[i], values[i]);
        }
    }

    // Store trade details when opened (acc fee values)
    function storeTradeInitialAccFees(
        address trader,
        uint pairIndex,
        uint index,
        bool long
    ) external {
        storeAccFundingFees(pairIndex);

        TradeInitialAccFees storage t = tradeInitialAccFees[trader][pairIndex][
            index
        ];

        t.rollover = getPendingAccRolloverFees(pairIndex);

        t.funding = long
            ? pairFundingFees[pairIndex].accPerOiLong
            : pairFundingFees[pairIndex].accPerOiShort;

        t.openedAfterUpdate = true;

        emit TradeInitialAccFeesStored(
            trader,
            pairIndex,
            index,
            t.rollover,
            t.funding
        );
    }

    // Acc rollover fees (store right before fee % update)
    function storeAccRolloverFees(uint pairIndex) private {
        PairRolloverFees storage r = pairRolloverFees[pairIndex];

        r.accPerCollateral = getPendingAccRolloverFees(pairIndex);
        r.lastUpdateBlock = block.number;

        emit AccRolloverFeesStored(pairIndex, r.accPerCollateral);
    }

    function getPendingAccRolloverFees(
        uint pairIndex
    ) public view returns (uint) {
        // 1e18 (DAI)
        PairRolloverFees storage r = pairRolloverFees[pairIndex];

        return
            r.accPerCollateral +
            ((block.number - r.lastUpdateBlock) *
                pairParams[pairIndex].rolloverFeePerBlockP *
                1e18) /
            PRECISION /
            100;
    }

    // Acc funding fees (store right before trades opened / closed and fee % update)
    function storeAccFundingFees(uint pairIndex) private {
        PairFundingFees storage f = pairFundingFees[pairIndex];

        (f.accPerOiLong, f.accPerOiShort) = getPendingAccFundingFees(pairIndex);
        f.lastUpdateBlock = block.number;

        emit AccFundingFeesStored(pairIndex, f.accPerOiLong, f.accPerOiShort);
    }

    function getPendingAccFundingFees(
        uint pairIndex
    ) public view returns (int valueLong, int valueShort) {
        PairFundingFees storage f = pairFundingFees[pairIndex];

        valueLong = f.accPerOiLong;
        valueShort = f.accPerOiShort;

        int openInterestDaiLong = int(storageT.openInterestDai(pairIndex, 0));
        int openInterestDaiShort = int(storageT.openInterestDai(pairIndex, 1));

        int fundingFeesPaidByLongs = ((openInterestDaiLong -
            openInterestDaiShort) *
            int(block.number - f.lastUpdateBlock) *
            int(pairParams[pairIndex].fundingFeePerBlockP)) /
            int(PRECISION) /
            100;

        if (openInterestDaiLong > 0) {
            valueLong += (fundingFeesPaidByLongs * 1e18) / openInterestDaiLong;
        }

        if (openInterestDaiShort > 0) {
            valueShort +=
                (fundingFeesPaidByLongs * 1e18 * (-1)) /
                openInterestDaiShort;
        }
    }

    // Dynamic price impact value on trade opening
    function getTradePriceImpact(
        uint openPrice, // PRECISION
        uint pairIndex,
        bool long,
        uint tradeOpenInterest // 1e18 (DAI)
    )
        external
        view
        returns (
            uint priceImpactP, // PRECISION (%)
            uint priceAfterImpact // PRECISION
        )
    {
        (priceImpactP, priceAfterImpact) = getTradePriceImpactPure(
            openPrice,
            long,
            storageT.openInterestDai(pairIndex, long ? 0 : 1),
            tradeOpenInterest,
            long
                ? pairParams[pairIndex].onePercentDepthAbove
                : pairParams[pairIndex].onePercentDepthBelow
        );
    }

    function getTradePriceImpactPure(
        uint openPrice, // PRECISION
        bool long,
        uint startOpenInterest, // 1e18 (DAI)
        uint tradeOpenInterest, // 1e18 (DAI)
        uint onePercentDepth
    )
        public
        pure
        returns (
            uint priceImpactP, // PRECISION (%)
            uint priceAfterImpact // PRECISION
        )
    {
        if (onePercentDepth == 0) {
            return (0, openPrice);
        }

        priceImpactP =
            ((startOpenInterest + tradeOpenInterest / 2) * PRECISION) /
            1e18 /
            onePercentDepth;

        uint priceImpact = (priceImpactP * openPrice) / PRECISION / 100;

        priceAfterImpact = long
            ? openPrice + priceImpact
            : openPrice - priceImpact;
    }

    // Rollover fee value
    function getTradeRolloverFee(
        address trader,
        uint pairIndex,
        uint index,
        uint collateral // 1e18 (DAI)
    ) public view returns (uint) {
        // 1e18 (DAI)
        TradeInitialAccFees memory t = tradeInitialAccFees[trader][pairIndex][
            index
        ];

        if (!t.openedAfterUpdate) {
            return 0;
        }

        return
            getTradeRolloverFeePure(
                t.rollover,
                getPendingAccRolloverFees(pairIndex),
                collateral
            );
    }

    function getTradeRolloverFeePure(
        uint accRolloverFeesPerCollateral,
        uint endAccRolloverFeesPerCollateral,
        uint collateral // 1e18 (DAI)
    ) public pure returns (uint) {
        // 1e18 (DAI)
        return
            ((endAccRolloverFeesPerCollateral - accRolloverFeesPerCollateral) *
                collateral) / 1e18;
    }

    // Funding fee value
    function getTradeFundingFee(
        address trader,
        uint pairIndex,
        uint index,
        bool long,
        uint collateral, // 1e18 (DAI)
        uint leverage
    )
        public
        view
        returns (
            int // 1e18 (DAI) | Positive => Fee, Negative => Reward
        )
    {
        TradeInitialAccFees memory t = tradeInitialAccFees[trader][pairIndex][
            index
        ];

        if (!t.openedAfterUpdate) {
            return 0;
        }

        (int pendingLong, int pendingShort) = getPendingAccFundingFees(
            pairIndex
        );

        return
            getTradeFundingFeePure(
                t.funding,
                long ? pendingLong : pendingShort,
                collateral,
                leverage
            );
    }

    function getTradeFundingFeePure(
        int accFundingFeesPerOi,
        int endAccFundingFeesPerOi,
        uint collateral, // 1e18 (DAI)
        uint leverage
    )
        public
        pure
        returns (
            int // 1e18 (DAI) | Positive => Fee, Negative => Reward
        )
    {
        return
            ((endAccFundingFeesPerOi - accFundingFeesPerOi) *
                int(collateral) *
                int(leverage)) / 1e18;
    }

    // Liquidation price value after rollover and funding fees
    function getTradeLiquidationPrice(
        address trader,
        uint pairIndex,
        uint index,
        uint openPrice, // PRECISION
        bool long,
        uint collateral, // 1e18 (DAI)
        uint leverage
    ) external view returns (uint) {
        // PRECISION
        return
            getTradeLiquidationPricePure(
                openPrice,
                long,
                collateral,
                leverage,
                getTradeRolloverFee(trader, pairIndex, index, collateral),
                getTradeFundingFee(
                    trader,
                    pairIndex,
                    index,
                    long,
                    collateral,
                    leverage
                )
            );
    }

    function getTradeLiquidationPricePure(
        uint openPrice, // PRECISION
        bool long,
        uint collateral, // 1e18 (DAI)
        uint leverage,
        uint rolloverFee, // 1e18 (DAI)
        int fundingFee // 1e18 (DAI)
    ) public pure returns (uint) {
        // PRECISION
        int liqPriceDistance = (int(openPrice) *
            (int((collateral * LIQ_THRESHOLD_P) / 100) -
                int(rolloverFee) -
                fundingFee)) /
            int(collateral) /
            int(leverage);

        int liqPrice = long
            ? int(openPrice) - liqPriceDistance
            : int(openPrice) + liqPriceDistance;

        return liqPrice > 0 ? uint(liqPrice) : 0;
    }

    // Dai sent to trader after PnL and fees
    function getTradeValue(
        address trader,
        uint pairIndex,
        uint index,
        bool long,
        uint collateral, // 1e18 (DAI)
        uint leverage,
        int percentProfit, // PRECISION (%)
        uint closingFee // 1e18 (DAI)
    ) external onlyCallbacks returns (uint amount) {
        // 1e18 (DAI)
        storeAccFundingFees(pairIndex);

        uint r = getTradeRolloverFee(trader, pairIndex, index, collateral);
        int f = getTradeFundingFee(
            trader,
            pairIndex,
            index,
            long,
            collateral,
            leverage
        );

        amount = getTradeValuePure(collateral, percentProfit, r, f, closingFee);

        emit FeesCharged(
            pairIndex,
            long,
            collateral,
            leverage,
            percentProfit,
            r,
            f
        );
    }

    function getTradeValuePure(
        uint collateral, // 1e18 (DAI)
        int percentProfit, // PRECISION (%)
        uint rolloverFee, // 1e18 (DAI)
        int fundingFee, // 1e18 (DAI)
        uint closingFee // 1e18 (DAI)
    ) public pure returns (uint) {
        // 1e18 (DAI)
        int value = int(collateral) +
            (int(collateral) * percentProfit) /
            int(PRECISION) /
            100 -
            int(rolloverFee) -
            fundingFee;

        if (value <= (int(collateral) * int(100 - LIQ_THRESHOLD_P)) / 100) {
            return 0;
        }

        value -= int(closingFee);

        return value > 0 ? uint(value) : 0;
    }

    // Useful getters
    function getPairInfos(
        uint[] memory indices
    )
        external
        view
        returns (
            PairParams[] memory,
            PairRolloverFees[] memory,
            PairFundingFees[] memory
        )
    {
        PairParams[] memory params = new PairParams[](indices.length);
        PairRolloverFees[] memory rolloverFees = new PairRolloverFees[](
            indices.length
        );
        PairFundingFees[] memory fundingFees = new PairFundingFees[](
            indices.length
        );

        for (uint i = 0; i < indices.length; i++) {
            uint index = indices[i];

            params[i] = pairParams[index];
            rolloverFees[i] = pairRolloverFees[index];
            fundingFees[i] = pairFundingFees[index];
        }

        return (params, rolloverFees, fundingFees);
    }

    function getOnePercentDepthAbove(
        uint pairIndex
    ) external view returns (uint) {
        return pairParams[pairIndex].onePercentDepthAbove;
    }

    function getOnePercentDepthBelow(
        uint pairIndex
    ) external view returns (uint) {
        return pairParams[pairIndex].onePercentDepthBelow;
    }

    function getRolloverFeePerBlockP(
        uint pairIndex
    ) external view returns (uint) {
        return pairParams[pairIndex].rolloverFeePerBlockP;
    }

    function getFundingFeePerBlockP(
        uint pairIndex
    ) external view returns (uint) {
        return pairParams[pairIndex].fundingFeePerBlockP;
    }

    function getAccRolloverFees(uint pairIndex) external view returns (uint) {
        return pairRolloverFees[pairIndex].accPerCollateral;
    }

    function getAccRolloverFeesUpdateBlock(
        uint pairIndex
    ) external view returns (uint) {
        return pairRolloverFees[pairIndex].lastUpdateBlock;
    }

    function getAccFundingFeesLong(uint pairIndex) external view returns (int) {
        return pairFundingFees[pairIndex].accPerOiLong;
    }

    function getAccFundingFeesShort(
        uint pairIndex
    ) external view returns (int) {
        return pairFundingFees[pairIndex].accPerOiShort;
    }

    function getAccFundingFeesUpdateBlock(
        uint pairIndex
    ) external view returns (uint) {
        return pairFundingFees[pairIndex].lastUpdateBlock;
    }

    function getTradeInitialAccRolloverFeesPerCollateral(
        address trader,
        uint pairIndex,
        uint index
    ) external view returns (uint) {
        return tradeInitialAccFees[trader][pairIndex][index].rollover;
    }

    function getTradeInitialAccFundingFeesPerOi(
        address trader,
        uint pairIndex,
        uint index
    ) external view returns (int) {
        return tradeInitialAccFees[trader][pairIndex][index].funding;
    }

    function getTradeOpenedAfterUpdate(
        address trader,
        uint pairIndex,
        uint index
    ) external view returns (bool) {
        return tradeInitialAccFees[trader][pairIndex][index].openedAfterUpdate;
    }
}

contracts/contracts/GNSPairStorageV6.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../interfaces/StorageInterfaceV5.sol";

contract GNSPairsStorageV6 {
    // Params (constant)
    uint constant MIN_LEVERAGE = 2;
    uint constant MAX_LEVERAGE = 1000;

    // Custom data types
    enum FeedCalculation {
        DEFAULT,
        INVERT,
        COMBINE
    }
    struct Feed {
        address feed1;
        address feed2;
        FeedCalculation feedCalculation;
        uint maxDeviationP;
    } // PRECISION (%)

    struct Pair {
        string from;
        string to;
        Feed feed;
        uint spreadP; // PRECISION
        uint groupIndex;
        uint feeIndex;
    }
    struct Group {
        string name;
        bytes32 job;
        uint minLeverage;
        uint maxLeverage;
        uint maxCollateralP; // % (of DAI vault current balance)
    }
    struct Fee {
        string name;
        uint openFeeP; // PRECISION (% of leveraged pos)
        uint closeFeeP; // PRECISION (% of leveraged pos)
        uint oracleFeeP; // PRECISION (% of leveraged pos)
        uint nftLimitOrderFeeP; // PRECISION (% of leveraged pos)
        uint referralFeeP; // PRECISION (% of leveraged pos)
        uint minLevPosDai; // 1e18 (collateral x leverage, useful for min fee)
    }

    // State
    uint public currentOrderId;
    uint public pairsCount;
    uint public groupsCount;
    uint public feesCount;

    StorageInterfaceV5 public storageT;

    mapping(uint => Pair) public pairs;
    mapping(uint => Group) public groups;
    mapping(uint => Fee) public fees;

    mapping(string => mapping(string => bool)) public isPairListed;
    mapping(uint => uint[2]) public groupsCollaterals; // (long, short)

    // Events
    event PairAdded(uint index, string from, string to);
    event PairUpdated(uint index);

    event GroupAdded(uint index, string name);
    event GroupUpdated(uint index);

    event FeeAdded(uint index, string name);
    event FeeUpdated(uint index);

    constructor(uint _currentOrderId, StorageInterfaceV5 _storageT) {
        require(_currentOrderId > 0, "ORDER_ID_0");
        currentOrderId = _currentOrderId;
        storageT = _storageT;
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }

    modifier groupListed(uint _groupIndex) {
        require(groups[_groupIndex].minLeverage > 0, "GROUP_NOT_LISTED");
        _;
    }
    modifier feeListed(uint _feeIndex) {
        require(fees[_feeIndex].openFeeP > 0, "FEE_NOT_LISTED");
        _;
    }

    modifier feedOk(Feed calldata _feed) {
        require(
            _feed.maxDeviationP > 0 && _feed.feed1 != address(0),
            "WRONG_FEED"
        );
        require(
            _feed.feedCalculation != FeedCalculation.COMBINE ||
                _feed.feed2 != address(0),
            "FEED_2_MISSING"
        );
        _;
    }
    modifier groupOk(Group calldata _group) {
        require(_group.job != bytes32(0), "JOB_EMPTY");
        require(
            _group.minLeverage >= MIN_LEVERAGE &&
                _group.maxLeverage <= MAX_LEVERAGE &&
                _group.minLeverage < _group.maxLeverage,
            "WRONG_LEVERAGES"
        );
        _;
    }
    modifier feeOk(Fee calldata _fee) {
        require(
            _fee.openFeeP > 0 &&
                _fee.closeFeeP > 0 &&
                _fee.oracleFeeP > 0 &&
                _fee.nftLimitOrderFeeP > 0 &&
                _fee.referralFeeP > 0 &&
                _fee.minLevPosDai > 0,
            "WRONG_FEES"
        );
        _;
    }

    // Manage pairs
    function addPair(
        Pair calldata _pair
    )
        public
        onlyGov
        feedOk(_pair.feed)
        groupListed(_pair.groupIndex)
        feeListed(_pair.feeIndex)
    {
        require(!isPairListed[_pair.from][_pair.to], "PAIR_ALREADY_LISTED");

        pairs[pairsCount] = _pair;
        isPairListed[_pair.from][_pair.to] = true;

        emit PairAdded(pairsCount++, _pair.from, _pair.to);
    }

    function addPairs(Pair[] calldata _pairs) external {
        for (uint i = 0; i < _pairs.length; i++) {
            addPair(_pairs[i]);
        }
    }

    function updatePair(
        uint _pairIndex,
        Pair calldata _pair
    ) external onlyGov feedOk(_pair.feed) feeListed(_pair.feeIndex) {
        Pair storage p = pairs[_pairIndex];
        require(isPairListed[p.from][p.to], "PAIR_NOT_LISTED");

        p.feed = _pair.feed;
        p.spreadP = _pair.spreadP;
        p.feeIndex = _pair.feeIndex;

        emit PairUpdated(_pairIndex);
    }

    // Manage groups
    function addGroup(Group calldata _group) external onlyGov groupOk(_group) {
        groups[groupsCount] = _group;
        emit GroupAdded(groupsCount++, _group.name);
    }

    function updateGroup(
        uint _id,
        Group calldata _group
    ) external onlyGov groupListed(_id) groupOk(_group) {
        groups[_id] = _group;
        emit GroupUpdated(_id);
    }

    // Manage fees
    function addFee(Fee calldata _fee) external onlyGov feeOk(_fee) {
        fees[feesCount] = _fee;
        emit FeeAdded(feesCount++, _fee.name);
    }

    function updateFee(
        uint _id,
        Fee calldata _fee
    ) external onlyGov feeListed(_id) feeOk(_fee) {
        fees[_id] = _fee;
        emit FeeUpdated(_id);
    }

    // Update collateral open exposure for a group (callbacks)
    function updateGroupCollateral(
        uint _pairIndex,
        uint _amount,
        bool _long,
        bool _increase
    ) external {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");

        uint[2] storage collateralOpen = groupsCollaterals[
            pairs[_pairIndex].groupIndex
        ];
        uint index = _long ? 0 : 1;

        if (_increase) {
            collateralOpen[index] += _amount;
        } else {
            collateralOpen[index] = collateralOpen[index] > _amount
                ? collateralOpen[index] - _amount
                : 0;
        }
    }

    // Fetch relevant info for order (aggregator)
    function pairJob(
        uint _pairIndex
    ) external returns (string memory, string memory, bytes32, uint) {
        require(
            msg.sender == address(storageT.priceAggregator()),
            "AGGREGATOR_ONLY"
        );

        Pair memory p = pairs[_pairIndex];
        require(isPairListed[p.from][p.to], "PAIR_NOT_LISTED");

        return (p.from, p.to, groups[p.groupIndex].job, currentOrderId++);
    }

    // Getters (pairs & groups)
    function pairFeed(uint _pairIndex) external view returns (Feed memory) {
        return pairs[_pairIndex].feed;
    }

    function pairSpreadP(uint _pairIndex) external view returns (uint) {
        return pairs[_pairIndex].spreadP;
    }

    function pairMinLeverage(uint _pairIndex) external view returns (uint) {
        return groups[pairs[_pairIndex].groupIndex].minLeverage;
    }

    function pairMaxLeverage(uint _pairIndex) external view returns (uint) {
        return groups[pairs[_pairIndex].groupIndex].maxLeverage;
    }

    function groupMaxCollateral(uint _pairIndex) external view returns (uint) {
        return
            (groups[pairs[_pairIndex].groupIndex].maxCollateralP *
                storageT.vault().currentBalanceDai()) / 100;
    }

    function groupCollateral(
        uint _pairIndex,
        bool _long
    ) external view returns (uint) {
        return groupsCollaterals[pairs[_pairIndex].groupIndex][_long ? 0 : 1];
    }

    function guaranteedSlEnabled(uint _pairIndex) external view returns (bool) {
        return pairs[_pairIndex].groupIndex == 0; // crypto only
    }

    // Getters (fees)
    function pairOpenFeeP(uint _pairIndex) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].openFeeP;
    }

    function pairCloseFeeP(uint _pairIndex) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].closeFeeP;
    }

    function pairOracleFeeP(uint _pairIndex) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].oracleFeeP;
    }

    function pairNftLimitOrderFeeP(
        uint _pairIndex
    ) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].nftLimitOrderFeeP;
    }

    function pairReferralFeeP(uint _pairIndex) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].referralFeeP;
    }

    function pairMinLevPosDai(uint _pairIndex) external view returns (uint) {
        return fees[pairs[_pairIndex].feeIndex].minLevPosDai;
    }

    // Getters (backend)
    function pairsBackend(
        uint _index
    ) external view returns (Pair memory, Group memory, Fee memory) {
        Pair memory p = pairs[_index];
        return (p, groups[p.groupIndex], fees[p.feeIndex]);
    }
}

contracts/contracts/GNSPriceAggregatorV6.content:
// SPDX-License-Identifier: MIT
import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import "../v6.3/TWAPPriceGetter.sol";

import "../interfaces/CallbacksInterfaceV6_4.sol";
import "../interfaces/ChainlinkFeedInterfaceV5.sol";
import "../interfaces/StorageInterfaceV5.sol";

import "../libraries/PackingUtils.sol";

pragma solidity ^0.8.7;

contract GNSPriceAggregatorV6_4 is ChainlinkClient /*, TWAPPriceGetter*/ {
    using Chainlink for Chainlink.Request;
    using PackingUtils for uint256;

    // Contracts (constant)
    StorageInterfaceV5 public immutable storageT;

    // Contracts (adjustable)
    PairsStorageInterfaceV6 public pairsStorage;
    ChainlinkFeedInterfaceV5 public linkPriceFeed;

    // Params (constant)
    uint constant PRECISION = 1e10;
    uint constant MAX_ORACLE_NODES = 20;
    uint constant MIN_ANSWERS = 1;

    // Params (adjustable)
    uint public minAnswers;

    // Custom data types
    enum OrderType {
        MARKET_OPEN,
        MARKET_CLOSE,
        LIMIT_OPEN,
        LIMIT_CLOSE
    }

    struct Order {
        uint16 pairIndex;
        uint112 linkFeePerNode;
        OrderType orderType;
        bool active;
        bool isLookback;
    }

    struct LookbackOrderAnswer {
        uint64 open;
        uint64 high;
        uint64 low;
        uint64 ts;
    }

    // State
    address[] public nodes;
    bytes32[2] public jobIds;

    mapping(uint => Order) public orders;
    mapping(bytes32 => uint) public orderIdByRequest;
    mapping(uint => uint[]) public ordersAnswers;
    mapping(uint => LookbackOrderAnswer[]) public lookbackOrderAnswers;

    // Events
    event PairsStorageUpdated(address value);
    event LinkPriceFeedUpdated(address value);
    event MinAnswersUpdated(uint value);

    event NodeAdded(uint index, address value);
    event NodeReplaced(uint index, address oldNode, address newNode);
    event NodeRemoved(uint index, address oldNode);

    event JobIdUpdated(uint index, bytes32 jobId);

    event PriceRequested(
        uint indexed orderId,
        bytes32 indexed job,
        uint indexed pairIndex,
        OrderType orderType,
        uint nodesCount,
        uint linkFeePerNode,
        uint fromBlock,
        bool isLookback
    );

    event PriceReceived(
        bytes32 request,
        uint indexed orderId,
        address indexed node,
        uint16 indexed pairIndex,
        uint price,
        uint referencePrice,
        uint112 linkFee,
        bool isLookback,
        bool usedInMedian
    );

    event CallbackExecuted(
        CallbacksInterfaceV6_4.AggregatorAnswer a,
        OrderType orderType
    );

    constructor(
        address _linkToken,
        IUniswapV3Pool _tokenDaiLp,
        uint32 _twapInterval,
        StorageInterfaceV5 _storageT,
        PairsStorageInterfaceV6 _pairsStorage,
        ChainlinkFeedInterfaceV5 _linkPriceFeed,
        uint _minAnswers,
        address[] memory _nodes,
        bytes32[2] memory _jobIds // TWAPPriceGetter(
    ) //     _tokenDaiLp,
    //     address(_storageT.token()),
    //     _twapInterval,
    //     PRECISION
    // )
    {
        require(
            address(_storageT) != address(0) &&
                address(_pairsStorage) != address(0) &&
                address(_linkPriceFeed) != address(0) &&
                _minAnswers >= MIN_ANSWERS &&
                _minAnswers % 2 == 1 &&
                _nodes.length > 0 &&
                _linkToken != address(0),
            "WRONG_PARAMS"
        );

        storageT = _storageT;

        pairsStorage = _pairsStorage;
        linkPriceFeed = _linkPriceFeed;

        minAnswers = _minAnswers;
        nodes = _nodes;
        jobIds = _jobIds;

        setChainlinkToken(_linkToken);
    }

    // Modifiers
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyTrading() {
        require(msg.sender == storageT.trading(), "TRADING_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");
        _;
    }

    // Manage contracts
    function updatePairsStorage(
        PairsStorageInterfaceV6 value
    ) external onlyGov {
        require(address(value) != address(0), "VALUE_0");

        pairsStorage = value;

        emit PairsStorageUpdated(address(value));
    }

    function updateLinkPriceFeed(
        ChainlinkFeedInterfaceV5 value
    ) external onlyGov {
        require(address(value) != address(0), "VALUE_0");

        linkPriceFeed = value;

        emit LinkPriceFeedUpdated(address(value));
    }

    // // Manage TWAP variables
    // function updateUniV3Pool(IUniswapV3Pool _uniV3Pool) external onlyGov {
    //     _updateUniV3Pool(_uniV3Pool);
    // }

    // function updateTwapInterval(uint32 _twapInterval) external onlyGov {
    //     _updateTwapInterval(_twapInterval);
    // }

    // Manage params
    function updateMinAnswers(uint value) external onlyGov {
        require(value >= MIN_ANSWERS, "MIN_ANSWERS");
        require(value % 2 == 1, "EVEN");

        minAnswers = value;

        emit MinAnswersUpdated(value);
    }

    // Manage nodes
    function addNode(address a) external onlyGov {
        require(a != address(0), "VALUE_0");
        require(nodes.length < MAX_ORACLE_NODES, "MAX_ORACLE_NODES");

        for (uint i; i < nodes.length; i++) {
            require(nodes[i] != a, "ALREADY_LISTED");
        }

        nodes.push(a);

        emit NodeAdded(nodes.length - 1, a);
    }

    function replaceNode(uint index, address a) external onlyGov {
        require(index < nodes.length, "WRONG_INDEX");
        require(a != address(0), "VALUE_0");

        emit NodeReplaced(index, nodes[index], a);

        nodes[index] = a;
    }

    function removeNode(uint index) external onlyGov {
        require(index < nodes.length, "WRONG_INDEX");

        emit NodeRemoved(index, nodes[index]);

        nodes[index] = nodes[nodes.length - 1];
        nodes.pop();
    }

    function setMarketJobId(bytes32 jobId) external onlyGov {
        require(jobId != bytes32(0), "VALUE_0");

        jobIds[0] = jobId;

        emit JobIdUpdated(0, jobId);
    }

    function setLimitJobId(bytes32 jobId) external onlyGov {
        require(jobId != bytes32(0), "VALUE_0");

        jobIds[1] = jobId;

        emit JobIdUpdated(1, jobId);
    }

    // On-demand price request to oracles network
    function getPrice(
        uint pairIndex,
        OrderType orderType,
        uint leveragedPosDai,
        uint fromBlock
    ) external onlyTrading returns (uint) {
        require(pairIndex <= type(uint16).max, "PAIR_OVERFLOW");

        bool isLookback = orderType == OrderType.LIMIT_OPEN ||
            orderType == OrderType.LIMIT_CLOSE;
        bytes32 job = isLookback ? jobIds[1] : jobIds[0];

        Chainlink.Request memory linkRequest = buildChainlinkRequest(
            job,
            address(this),
            this.fulfill.selector
        );

        uint orderId;
        {
            (
                string memory from,
                string memory to,
                ,
                uint _orderId
            ) = pairsStorage.pairJob(pairIndex);
            orderId = _orderId;

            linkRequest.add("from", from);
            linkRequest.add("to", to);

            if (isLookback) {
                linkRequest.addUint("fromBlock", fromBlock);
            }
        }

        uint length;
        uint linkFeePerNode;
        {
            address[] memory _nodes = nodes;
            length = _nodes.length;
            linkFeePerNode = linkFee(pairIndex, leveragedPosDai) / length;

            require(linkFeePerNode <= type(uint112).max, "LINK_OVERFLOW");

            orders[orderId] = Order(
                uint16(pairIndex),
                uint112(linkFeePerNode),
                orderType,
                true,
                isLookback
            );
            for (uint i; i < length; ) {
                orderIdByRequest[
                    sendChainlinkRequestTo(
                        _nodes[i],
                        linkRequest,
                        linkFeePerNode
                    )
                ] = orderId;
                unchecked {
                    ++i;
                }
            }
        }

        emit PriceRequested(
            orderId,
            job,
            pairIndex,
            orderType,
            length,
            linkFeePerNode,
            fromBlock,
            isLookback
        );

        return orderId;
    }

    // Fulfill on-demand price requests
    function fulfill(bytes32 requestId, uint256 priceData) external {
        uint orderId = orderIdByRequest[requestId];
        delete orderIdByRequest[requestId];

        Order memory r = orders[orderId];
        bool usedInMedian = false;

        PairsStorageInterfaceV6.Feed memory f = pairsStorage.pairFeed(
            r.pairIndex
        );
        uint feedPrice = fetchFeedPrice(f);

        if (r.active) {
            if (r.isLookback) {
                LookbackOrderAnswer memory newAnswer;
                (
                    newAnswer.open,
                    newAnswer.high,
                    newAnswer.low,
                    newAnswer.ts
                ) = priceData.unpack256To64();

                require(
                    (newAnswer.high == 0 && newAnswer.low == 0) ||
                        (newAnswer.high >= newAnswer.open &&
                            newAnswer.low <= newAnswer.open &&
                            newAnswer.low > 0),
                    "INVALID_CANDLE"
                );

                if (
                    isPriceWithinDeviation(
                        newAnswer.high,
                        feedPrice,
                        f.maxDeviationP
                    ) &&
                    isPriceWithinDeviation(
                        newAnswer.low,
                        feedPrice,
                        f.maxDeviationP
                    )
                ) {
                    usedInMedian = true;

                    LookbackOrderAnswer[]
                        storage answers = lookbackOrderAnswers[orderId];
                    answers.push(newAnswer);

                    if (answers.length == minAnswers) {
                        CallbacksInterfaceV6_4.AggregatorAnswer memory a;
                        a.orderId = orderId;
                        (a.open, a.high, a.low) = medianLookbacks(answers);
                        a.spreadP = pairsStorage.pairSpreadP(r.pairIndex);

                        CallbacksInterfaceV6_4 c = CallbacksInterfaceV6_4(
                            storageT.callbacks()
                        );

                        if (r.orderType == OrderType.LIMIT_OPEN) {
                            c.executeNftOpenOrderCallback(a);
                        } else {
                            c.executeNftCloseOrderCallback(a);
                        }

                        emit CallbackExecuted(a, r.orderType);

                        orders[orderId].active = false;
                        delete lookbackOrderAnswers[orderId];
                    }
                }
            } else {
                (uint64 price, , , ) = priceData.unpack256To64();

                if (isPriceWithinDeviation(price, feedPrice, f.maxDeviationP)) {
                    usedInMedian = true;

                    uint[] storage answers = ordersAnswers[orderId];
                    answers.push(price);

                    if (answers.length == minAnswers) {
                        CallbacksInterfaceV6_4.AggregatorAnswer memory a;

                        a.orderId = orderId;
                        a.price = median(answers);
                        a.spreadP = pairsStorage.pairSpreadP(r.pairIndex);

                        CallbacksInterfaceV6_4 c = CallbacksInterfaceV6_4(
                            storageT.callbacks()
                        );

                        if (r.orderType == OrderType.MARKET_OPEN) {
                            c.openTradeMarketCallback(a);
                        } else {
                            c.closeTradeMarketCallback(a);
                        }

                        emit CallbackExecuted(a, r.orderType);

                        orders[orderId].active = false;
                        delete ordersAnswers[orderId];
                    }
                }
            }
        }

        emit PriceReceived(
            requestId,
            orderId,
            msg.sender,
            r.pairIndex,
            priceData,
            feedPrice,
            r.linkFeePerNode,
            r.isLookback,
            usedInMedian
        );
    }

    // Calculate LINK fee for each request
    function linkFee(
        uint pairIndex,
        uint leveragedPosDai
    ) public view returns (uint) {
        (, int linkPriceUsd, , , ) = linkPriceFeed.latestRoundData();

        return
            (pairsStorage.pairOracleFeeP(pairIndex) * leveragedPosDai * 1e8) /
            uint(linkPriceUsd) /
            PRECISION /
            100;
    }

    // Claim back LINK tokens (if contract will be replaced for example)
    function claimBackLink() external onlyGov {
        TokenInterfaceV5 link = storageT.linkErc677();

        link.transfer(storageT.gov(), link.balanceOf(address(this)));
    }

    // Utils
    function fetchFeedPrice(
        PairsStorageInterfaceV6.Feed memory f
    ) private view returns (uint) {
        if (f.feed1 == address(0)) {
            return 0;
        }

        uint feedPrice;
        (, int feedPrice1, , , ) = ChainlinkFeedInterfaceV5(f.feed1)
            .latestRoundData();

        if (
            f.feedCalculation == PairsStorageInterfaceV6.FeedCalculation.DEFAULT
        ) {
            feedPrice = uint((feedPrice1 * int(PRECISION)) / 1e8);
        } else if (
            f.feedCalculation == PairsStorageInterfaceV6.FeedCalculation.INVERT
        ) {
            feedPrice = uint((int(PRECISION) * 1e8) / feedPrice1);
        } else {
            (, int feedPrice2, , , ) = ChainlinkFeedInterfaceV5(f.feed2)
                .latestRoundData();
            feedPrice = uint((feedPrice1 * int(PRECISION)) / feedPrice2);
        }

        return feedPrice;
    }

    function isPriceWithinDeviation(
        uint price,
        uint feedPrice,
        uint maxDeviationP
    ) private pure returns (bool) {
        return
            price == 0 ||
            feedPrice == 0 ||
            ((price >= feedPrice ? price - feedPrice : feedPrice - price) *
                PRECISION *
                100) /
                feedPrice <=
            maxDeviationP;
    }

    // Median function
    function swap(uint[] memory array, uint i, uint j) private pure {
        (array[i], array[j]) = (array[j], array[i]);
    }

    function sort(uint[] memory array, uint begin, uint end) private pure {
        if (begin >= end) {
            return;
        }

        uint j = begin;
        uint pivot = array[j];

        for (uint i = begin + 1; i < end; ++i) {
            if (array[i] < pivot) {
                swap(array, i, ++j);
            }
        }

        swap(array, begin, j);
        sort(array, begin, j);
        sort(array, j + 1, end);
    }

    function median(uint[] memory array) private pure returns (uint) {
        sort(array, 0, array.length);

        return
            array.length % 2 == 0
                ? (array[array.length / 2 - 1] + array[array.length / 2]) / 2
                : array[array.length / 2];
    }

    function medianLookbacks(
        LookbackOrderAnswer[] memory array
    ) private pure returns (uint open, uint high, uint low) {
        uint length = array.length;

        uint[] memory opens = new uint[](length);
        uint[] memory highs = new uint[](length);
        uint[] memory lows = new uint[](length);

        for (uint i; i < length; ) {
            opens[i] = array[i].open;
            highs[i] = array[i].high;
            lows[i] = array[i].low;

            unchecked {
                ++i;
            }
        }

        sort(opens, 0, length);
        sort(highs, 0, length);
        sort(lows, 0, length);

        bool isLengthEven = length % 2 == 0;
        uint halfLength = length / 2;

        open = isLengthEven
            ? (opens[halfLength - 1] + opens[halfLength]) / 2
            : opens[halfLength];
        high = isLengthEven
            ? (highs[halfLength - 1] + highs[halfLength]) / 2
            : highs[halfLength];
        low = isLengthEven
            ? (lows[halfLength - 1] + lows[halfLength]) / 2
            : lows[halfLength];
    }

    // Storage v5 compatibility
    function openFeeP(uint pairIndex) external view returns (uint) {
        return pairsStorage.pairOpenFeeP(pairIndex);
    }
}

contracts/contracts/GNSReferralsV6_2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../interfaces/StorageInterfaceV5.sol";

contract GNSReferralsV6_2 {
    // CONSTANTS
    uint constant PRECISION = 1e10;
    StorageInterfaceV5 public immutable storageT;

    // ADJUSTABLE PARAMETERS
    uint public allyFeeP; // % (of referrer fees going to allies, eg. 10)
    uint public startReferrerFeeP; // % (of referrer fee when 0 volume referred, eg. 75)
    uint public openFeeP; // % (of opening fee used for referral system, eg. 33)
    uint public targetVolumeDai; // DAI (to reach maximum referral system fee, eg. 1e8)

    // CUSTOM TYPES
    struct AllyDetails {
        address[] referrersReferred;
        uint volumeReferredDai; // 1e18
        uint pendingRewardsToken; // 1e18
        uint totalRewardsToken; // 1e18
        uint totalRewardsValueDai; // 1e18
        bool active;
    }

    struct ReferrerDetails {
        address ally;
        address[] tradersReferred;
        uint volumeReferredDai; // 1e18
        uint pendingRewardsToken; // 1e18
        uint totalRewardsToken; // 1e18
        uint totalRewardsValueDai; // 1e18
        bool active;
    }

    // STATE (MAPPINGS)
    mapping(address => AllyDetails) public allyDetails;
    mapping(address => ReferrerDetails) public referrerDetails;

    mapping(address => address) public referrerByTrader;

    // EVENTS
    event UpdatedAllyFeeP(uint value);
    event UpdatedStartReferrerFeeP(uint value);
    event UpdatedOpenFeeP(uint value);
    event UpdatedTargetVolumeDai(uint value);

    event AllyWhitelisted(address indexed ally);
    event AllyUnwhitelisted(address indexed ally);

    event ReferrerWhitelisted(address indexed referrer, address indexed ally);
    event ReferrerUnwhitelisted(address indexed referrer);
    event ReferrerRegistered(address indexed trader, address indexed referrer);

    event AllyRewardDistributed(
        address indexed ally,
        address indexed trader,
        uint volumeDai,
        uint amountToken,
        uint amountValueDai
    );
    event ReferrerRewardDistributed(
        address indexed referrer,
        address indexed trader,
        uint volumeDai,
        uint amountToken,
        uint amountValueDai
    );

    event AllyRewardsClaimed(address indexed ally, uint amountToken);
    event ReferrerRewardsClaimed(address indexed referrer, uint amountToken);

    constructor(
        StorageInterfaceV5 _storageT,
        uint _allyFeeP,
        uint _startReferrerFeeP,
        uint _openFeeP,
        uint _targetVolumeDai
    ) {
        require(
            address(_storageT) != address(0) &&
                _allyFeeP <= 50 &&
                _startReferrerFeeP <= 100 &&
                _openFeeP <= 50 &&
                _targetVolumeDai > 0,
            "WRONG_PARAMS"
        );

        storageT = _storageT;

        allyFeeP = _allyFeeP;
        startReferrerFeeP = _startReferrerFeeP;
        openFeeP = _openFeeP;
        targetVolumeDai = _targetVolumeDai;
    }

    // MODIFIERS
    modifier onlyGov() {
        require(msg.sender == storageT.gov(), "GOV_ONLY");
        _;
    }
    modifier onlyTrading() {
        require(msg.sender == storageT.trading(), "TRADING_ONLY");
        _;
    }
    modifier onlyCallbacks() {
        require(msg.sender == storageT.callbacks(), "CALLBACKS_ONLY");
        _;
    }

    // MANAGE PARAMETERS
    function updateAllyFeeP(uint value) external onlyGov {
        require(value <= 50, "VALUE_ABOVE_50");

        allyFeeP = value;

        emit UpdatedAllyFeeP(value);
    }

    function updateStartReferrerFeeP(uint value) external onlyGov {
        require(value <= 100, "VALUE_ABOVE_100");

        startReferrerFeeP = value;

        emit UpdatedStartReferrerFeeP(value);
    }

    function updateOpenFeeP(uint value) external onlyGov {
        require(value <= 50, "VALUE_ABOVE_50");

        openFeeP = value;

        emit UpdatedOpenFeeP(value);
    }

    function updateTargetVolumeDai(uint value) external onlyGov {
        require(value > 0, "VALUE_0");

        targetVolumeDai = value;

        emit UpdatedTargetVolumeDai(value);
    }

    // MANAGE ALLIES
    function whitelistAlly(address ally) external onlyGov {
        require(ally != address(0), "ADDRESS_0");

        AllyDetails storage a = allyDetails[ally];
        require(!a.active, "ALLY_ALREADY_ACTIVE");

        a.active = true;

        emit AllyWhitelisted(ally);
    }

    function unwhitelistAlly(address ally) external onlyGov {
        AllyDetails storage a = allyDetails[ally];
        require(a.active, "ALREADY_UNACTIVE");

        a.active = false;

        emit AllyUnwhitelisted(ally);
    }

    // MANAGE REFERRERS
    function whitelistReferrer(
        address referrer,
        address ally
    ) external onlyGov {
        require(referrer != address(0), "ADDRESS_0");

        ReferrerDetails storage r = referrerDetails[referrer];
        require(!r.active, "REFERRER_ALREADY_ACTIVE");

        r.active = true;

        if (ally != address(0)) {
            AllyDetails storage a = allyDetails[ally];
            require(a.active, "ALLY_NOT_ACTIVE");

            r.ally = ally;
            a.referrersReferred.push(referrer);
        }

        emit ReferrerWhitelisted(referrer, ally);
    }

    function unwhitelistReferrer(address referrer) external onlyGov {
        ReferrerDetails storage r = referrerDetails[referrer];
        require(r.active, "ALREADY_UNACTIVE");

        r.active = false;

        emit ReferrerUnwhitelisted(referrer);
    }

    function registerPotentialReferrer(
        address trader,
        address referrer
    ) external onlyTrading {
        ReferrerDetails storage r = referrerDetails[referrer];

        if (
            referrerByTrader[trader] != address(0) ||
            referrer == address(0) ||
            !r.active
        ) {
            return;
        }

        referrerByTrader[trader] = referrer;
        r.tradersReferred.push(trader);

        emit ReferrerRegistered(trader, referrer);
    }

    // REWARDS DISTRIBUTION
    function distributePotentialReward(
        address trader,
        uint volumeDai,
        uint pairOpenFeeP,
        uint tokenPriceDai
    ) external onlyCallbacks returns (uint) {
        address referrer = referrerByTrader[trader];
        ReferrerDetails storage r = referrerDetails[referrer];

        if (!r.active) {
            return 0;
        }

        uint referrerRewardValueDai = (volumeDai *
            getReferrerFeeP(pairOpenFeeP, r.volumeReferredDai)) /
            PRECISION /
            100;

        uint referrerRewardToken = (referrerRewardValueDai * PRECISION) /
            tokenPriceDai;

        storageT.handleTokens(address(this), referrerRewardToken, true);

        AllyDetails storage a = allyDetails[r.ally];

        uint allyRewardValueDai;
        uint allyRewardToken;

        if (a.active) {
            allyRewardValueDai = (referrerRewardValueDai * allyFeeP) / 100;
            allyRewardToken = (referrerRewardToken * allyFeeP) / 100;

            a.volumeReferredDai += volumeDai;
            a.pendingRewardsToken += allyRewardToken;
            a.totalRewardsToken += allyRewardToken;
            a.totalRewardsValueDai += allyRewardValueDai;

            referrerRewardValueDai -= allyRewardValueDai;
            referrerRewardToken -= allyRewardToken;

            emit AllyRewardDistributed(
                r.ally,
                trader,
                volumeDai,
                allyRewardToken,
                allyRewardValueDai
            );
        }

        r.volumeReferredDai += volumeDai;
        r.pendingRewardsToken += referrerRewardToken;
        r.totalRewardsToken += referrerRewardToken;
        r.totalRewardsValueDai += referrerRewardValueDai;

        emit ReferrerRewardDistributed(
            referrer,
            trader,
            volumeDai,
            referrerRewardToken,
            referrerRewardValueDai
        );

        return referrerRewardValueDai + allyRewardValueDai;
    }

    // REWARDS CLAIMING
    function claimAllyRewards() external {
        AllyDetails storage a = allyDetails[msg.sender];
        uint rewardsToken = a.pendingRewardsToken;

        require(rewardsToken > 0, "NO_PENDING_REWARDS");

        a.pendingRewardsToken = 0;
        storageT.token().transfer(msg.sender, rewardsToken);

        emit AllyRewardsClaimed(msg.sender, rewardsToken);
    }

    function claimReferrerRewards() external {
        ReferrerDetails storage r = referrerDetails[msg.sender];
        uint rewardsToken = r.pendingRewardsToken;

        require(rewardsToken > 0, "NO_PENDING_REWARDS");

        r.pendingRewardsToken = 0;
        storageT.token().transfer(msg.sender, rewardsToken);

        emit ReferrerRewardsClaimed(msg.sender, rewardsToken);
    }

    // VIEW FUNCTIONS
    function getReferrerFeeP(
        uint pairOpenFeeP,
        uint volumeReferredDai
    ) public view returns (uint) {
        uint maxReferrerFeeP = (pairOpenFeeP * 2 * openFeeP) / 100;
        uint minFeeP = (maxReferrerFeeP * startReferrerFeeP) / 100;

        uint feeP = minFeeP +
            ((maxReferrerFeeP - minFeeP) * volumeReferredDai) /
            1e18 /
            targetVolumeDai;

        return feeP > maxReferrerFeeP ? maxReferrerFeeP : feeP;
    }

    function getPercentOfOpenFeeP(address trader) external view returns (uint) {
        return
            getPercentOfOpenFeeP_calc(
                referrerDetails[referrerByTrader[trader]].volumeReferredDai
            );
    }

    function getPercentOfOpenFeeP_calc(
        uint volumeReferredDai
    ) public view returns (uint resultP) {
        resultP =
            (openFeeP *
                (startReferrerFeeP *
                    PRECISION +
                    (volumeReferredDai *
                        PRECISION *
                        (100 - startReferrerFeeP)) /
                    1e18 /
                    targetVolumeDai)) /
            100;

        resultP = resultP > openFeeP * PRECISION
            ? openFeeP * PRECISION
            : resultP;
    }

    function getTraderReferrer(address trader) external view returns (address) {
        address referrer = referrerByTrader[trader];

        return referrerDetails[referrer].active ? referrer : address(0);
    }

    function getReferrersReferred(
        address ally
    ) external view returns (address[] memory) {
        return allyDetails[ally].referrersReferred;
    }

    function getTradersReferred(
        address referred
    ) external view returns (address[] memory) {
        return referrerDetails[referred].tradersReferred;
    }
}

contracts/contracts/GNSTradingCallbacksV.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../interfaces/StorageInterfaceV5.sol";
import "../interfaces/GNSPairInfosInterfaceV6.sol";
import "../interfaces/GNSReferralsInterfaceV6_2.sol";
import "../interfaces/GNSStakingInterfaceV6_4_1.sol";
import "../interfaces/GNSBorrowingFeesInterfaceV6_4.sol";
import "../interfaces/IGNSOracleRewardsV6_4_1.sol";
import "../libraries/ChainUtils.sol";

contract GNSTradingCallbacksV6_4_1 is Initializable {
    // Contracts (constant)
    StorageInterfaceV5 public storageT;
    IGNSOracleRewardsV6_4_1 public nftRewards;
    GNSPairInfosInterfaceV6 public pairInfos;
    GNSReferralsInterfaceV6_2 public referrals;
    GNSStakingInterfaceV6_4_1 public staking;

    // Params (constant)
    uint private constant PRECISION = 1e10; // 10 decimals
    uint private constant MAX_SL_P = 75; // -75% PNL
    uint private constant MAX_GAIN_P = 900; // 900% PnL (10x)
    uint private constant MAX_EXECUTE_TIMEOUT = 5; // 5 blocks

    // Params (adjustable)
    uint public daiVaultFeeP; // % of closing fee going to DAI vault (eg. 40)
    uint public lpFeeP; // % of closing fee going to GNS/DAI LPs (eg. 20)
    uint public sssFeeP; // % of closing fee going to GNS staking (eg. 40)

    // State
    bool public isPaused; // Prevent opening new trades
    bool public isDone; // Prevent any interaction with the contract
    uint public canExecuteTimeout; // How long an update to TP/SL/Limit has to wait before it is executable (DEPRECATED)

    // Last Updated State
    mapping(address => mapping(uint => mapping(uint => mapping(TradeType => LastUpdated))))
        public tradeLastUpdated; // Block numbers for last updated

    // v6.3.2 Storage/State
    GNSBorrowingFeesInterfaceV6_4 public borrowingFees;
    mapping(uint => uint) public pairMaxLeverage;

    // v6.4 Storage
    mapping(address => mapping(uint => mapping(uint => mapping(TradeType => TradeData))))
        public tradeData; // More storage for trades / limit orders

    // v6.4.1 State
    uint public govFeesDai; // 1e18

    // Custom data types
    struct AggregatorAnswer {
        uint orderId;
        uint price;
        uint spreadP;
        uint open;
        uint high;
        uint low;
    }

    // Useful to avoid stack too deep errors
    struct Values {
        uint posDai;
        uint levPosDai;
        // uint tokenPriceDai;
        int profitP;
        uint price;
        uint liqPrice;
        uint daiSentToTrader;
        uint reward1;
        uint reward2;
        uint reward3;
        bool exactExecution;
    }

    struct SimplifiedTradeId {
        address trader;
        uint pairIndex;
        uint index;
        TradeType tradeType;
    }

    struct LastUpdated {
        uint32 tp;
        uint32 sl;
        uint32 limit;
        uint32 created;
    }

    struct TradeData {
        uint40 maxSlippageP; // 1e10 (%)
        uint216 _placeholder; // for potential future data
    }

    struct OpenTradePrepInput {
        uint executionPrice;
        uint wantedPrice;
        uint marketPrice;
        uint spreadP;
        bool buy;
        uint pairIndex;
        uint positionSize;
        uint leverage;
        uint maxSlippageP;
        uint tp;
        uint sl;
    }

    enum TradeType {
        MARKET,
        LIMIT
    }

    enum CancelReason {
        NONE,
        PAUSED,
        MARKET_CLOSED,
        SLIPPAGE,
        TP_REACHED,
        SL_REACHED,
        EXPOSURE_LIMITS,
        PRICE_IMPACT,
        MAX_LEVERAGE,
        NO_TRADE,
        WRONG_TRADE,
        NOT_HIT
    }

    // Events
    event MarketExecuted(
        uint indexed orderId,
        StorageInterfaceV5.Trade t,
        bool open,
        uint price,
        uint priceImpactP,
        uint positionSizeDai,
        int percentProfit, // before fees
        uint daiSentToTrader
    );

    event LimitExecuted(
        uint indexed orderId,
        uint limitIndex,
        StorageInterfaceV5.Trade t,
        address indexed nftHolder,
        StorageInterfaceV5.LimitOrder orderType,
        uint price,
        uint priceImpactP,
        uint positionSizeDai,
        int percentProfit,
        uint daiSentToTrader,
        bool exactExecution
    );

    event MarketOpenCanceled(
        uint indexed orderId,
        address indexed trader,
        uint indexed pairIndex,
        CancelReason cancelReason
    );
    event MarketCloseCanceled(
        uint indexed orderId,
        address indexed trader,
        uint indexed pairIndex,
        uint index,
        CancelReason cancelReason
    );
    event NftOrderCanceled(
        uint indexed orderId,
        address indexed nftHolder,
        StorageInterfaceV5.LimitOrder orderType,
        CancelReason cancelReason
    );

    event ClosingFeeSharesPUpdated(
        uint daiVaultFeeP,
        uint lpFeeP,
        uint sssFeeP
    );

    event Pause(bool paused);
    event Done(bool done);
    event GovFeesClaimed(uint valueDai);

    event GovFeeCharged(
        address indexed trader,
        uint valueDai,
        bool distributed
    );
    event ReferralFeeCharged(address indexed trader, uint valueDai);
    event TriggerFeeCharged(address indexed trader, uint valueDai);
    event SssFeeCharged(address indexed trader, uint valueDai);
    event DaiVaultFeeCharged(address indexed trader, uint valueDai);
    event BorrowingFeeCharged(
        address indexed trader,
        uint tradeValueDai,
        uint feeValueDai
    );
    event PairMaxLeverageUpdated(uint indexed pairIndex, uint maxLeverage);

    // Custom errors (save gas)
    error WrongParams();
    error Forbidden();

    function initialize(
        StorageInterfaceV5 _storageT,
        IGNSOracleRewardsV6_4_1 _nftRewards,
        GNSPairInfosInterfaceV6 _pairInfos,
        GNSReferralsInterfaceV6_2 _referrals,
        GNSStakingInterfaceV6_4_1 _staking,
        GNSBorrowingFeesInterfaceV6_4 _borrowingFees,
        address vaultToApprove,
        uint _daiVaultFeeP,
        uint _lpFeeP,
        uint _sssFeeP,
        uint _canExecuteTimeout
    ) external initializer {
        if (
            address(_storageT) == address(0) ||
            address(_nftRewards) == address(0) ||
            address(_pairInfos) == address(0) ||
            address(_referrals) == address(0) ||
            address(_staking) == address(0) ||
            address(_borrowingFees) == address(0) ||
            vaultToApprove == address(0) ||
            _daiVaultFeeP + _lpFeeP + _sssFeeP != 100 ||
            _canExecuteTimeout > MAX_EXECUTE_TIMEOUT
        ) {
            revert WrongParams();
        }

        storageT = _storageT;
        nftRewards = _nftRewards;
        pairInfos = _pairInfos;
        referrals = _referrals;
        staking = _staking;
        borrowingFees = _borrowingFees;

        daiVaultFeeP = _daiVaultFeeP;
        lpFeeP = _lpFeeP;
        sssFeeP = _sssFeeP;

        canExecuteTimeout = _canExecuteTimeout;

        TokenInterfaceV5 t = storageT.dai();
        t.approve(address(staking), type(uint256).max);
        t.approve(vaultToApprove, type(uint256).max);
    }

    // Modifiers
    modifier onlyGov() {
        _isGov();
        _;
    }
    modifier onlyPriceAggregator() {
        _isPriceAggregator();
        _;
    }
    modifier notDone() {
        _isNotDone();
        _;
    }
    modifier onlyTrading() {
        _isTrading();
        _;
    }
    modifier onlyManager() {
        _isManager();
        _;
    }

    // Saving code size by calling these functions inside modifiers
    function _isGov() private view {
        if (msg.sender != storageT.gov()) {
            revert Forbidden();
        }
    }

    function _isPriceAggregator() private view {
        if (msg.sender != address(storageT.priceAggregator())) {
            revert Forbidden();
        }
    }

    function _isNotDone() private view {
        if (isDone) {
            revert Forbidden();
        }
    }

    function _isTrading() private view {
        if (msg.sender != storageT.trading()) {
            revert Forbidden();
        }
    }

    function _isManager() private view {
        if (msg.sender != pairInfos.manager()) {
            revert Forbidden();
        }
    }

    // Manage params
    function setPairMaxLeverage(
        uint pairIndex,
        uint maxLeverage
    ) external onlyManager {
        _setPairMaxLeverage(pairIndex, maxLeverage);
    }

    function setPairMaxLeverageArray(
        uint[] calldata indices,
        uint[] calldata values
    ) external onlyManager {
        uint len = indices.length;

        if (len != values.length) {
            revert WrongParams();
        }

        for (uint i; i < len; ) {
            _setPairMaxLeverage(indices[i], values[i]);
            unchecked {
                ++i;
            }
        }
    }

    function _setPairMaxLeverage(uint pairIndex, uint maxLeverage) private {
        pairMaxLeverage[pairIndex] = maxLeverage;
        emit PairMaxLeverageUpdated(pairIndex, maxLeverage);
    }

    function setClosingFeeSharesP(
        uint _daiVaultFeeP,
        uint _lpFeeP,
        uint _sssFeeP
    ) external onlyGov {
        if (_daiVaultFeeP + _lpFeeP + _sssFeeP != 100) {
            revert WrongParams();
        }

        daiVaultFeeP = _daiVaultFeeP;
        lpFeeP = _lpFeeP;
        sssFeeP = _sssFeeP;

        emit ClosingFeeSharesPUpdated(_daiVaultFeeP, _lpFeeP, _sssFeeP);
    }

    // Manage state
    function pause() external onlyGov {
        isPaused = !isPaused;

        emit Pause(isPaused);
    }

    function done() external onlyGov {
        isDone = !isDone;

        emit Done(isDone);
    }

    // Claim fees
    function claimGovFees() external onlyGov {
        uint valueDai = govFeesDai;
        govFeesDai = 0;

        _transferFromStorageToAddress(storageT.gov(), valueDai);

        emit GovFeesClaimed(valueDai);
    }

    // Callbacks
    function openTradeMarketCallback(
        AggregatorAnswer memory a
    ) external onlyPriceAggregator notDone {
        StorageInterfaceV5.PendingMarketOrder memory o = _getPendingMarketOrder(
            a.orderId
        );

        if (o.block == 0) {
            return;
        }

        StorageInterfaceV5.Trade memory t = o.trade;

        (
            uint priceImpactP,
            uint priceAfterImpact,
            CancelReason cancelReason
        ) = _openTradePrep(
                OpenTradePrepInput(
                    a.price,
                    o.wantedPrice,
                    a.price,
                    a.spreadP,
                    t.buy,
                    t.pairIndex,
                    t.positionSizeDai,
                    t.leverage,
                    o.slippageP,
                    t.tp,
                    t.sl
                )
            );

        t.openPrice = priceAfterImpact;

        if (cancelReason == CancelReason.NONE) {
            (
                StorageInterfaceV5.Trade
                    memory finalTrade /*, uint tokenPriceDai */,

            ) = _registerTrade(t, false, 0);

            emit MarketExecuted(
                a.orderId,
                finalTrade,
                true,
                finalTrade.openPrice,
                priceImpactP,
                finalTrade.positionSizeDai,
                0,
                0
            );
        } else {
            // Gov fee to pay for oracle cost
            uint govFees = _handleGovFees(
                t.trader,
                t.pairIndex,
                t.positionSizeDai * t.leverage,
                true
            );
            _transferFromStorageToAddress(
                t.trader,
                t.positionSizeDai - govFees
            );

            emit MarketOpenCanceled(
                a.orderId,
                t.trader,
                t.pairIndex,
                cancelReason
            );
        }

        storageT.unregisterPendingMarketOrder(a.orderId, true);
    }

    function closeTradeMarketCallback(
        AggregatorAnswer memory a
    ) external onlyPriceAggregator notDone {
        StorageInterfaceV5.PendingMarketOrder memory o = _getPendingMarketOrder(
            a.orderId
        );

        if (o.block == 0) {
            return;
        }

        StorageInterfaceV5.Trade memory t = _getOpenTrade(
            o.trade.trader,
            o.trade.pairIndex,
            o.trade.index
        );

        CancelReason cancelReason = t.leverage == 0
            ? CancelReason.NO_TRADE
            : (a.price == 0 ? CancelReason.MARKET_CLOSED : CancelReason.NONE);

        if (cancelReason != CancelReason.NO_TRADE) {
            StorageInterfaceV5.TradeInfo memory i = _getOpenTradeInfo(
                t.trader,
                t.pairIndex,
                t.index
            );
            AggregatorInterfaceV6_4 aggregator = storageT.priceAggregator();

            Values memory v;
            // v.levPosDai =
            //     (t.initialPosToken * i.tokenPriceDai * t.leverage) /
            //     PRECISION;
            v.levPosDai = t.positionSizeDai * t.leverage;
            // v.tokenPriceDai = aggregator.tokenPriceDai();

            if (cancelReason == CancelReason.NONE) {
                v.profitP = _currentPercentProfit(
                    t.openPrice,
                    a.price,
                    t.buy,
                    t.leverage
                );
                v.posDai = v.levPosDai / t.leverage;

                v.daiSentToTrader = _unregisterTrade(
                    t,
                    true,
                    v.profitP,
                    v.posDai,
                    i.openInterestDai,
                    (v.levPosDai *
                        aggregator.pairsStorage().pairCloseFeeP(t.pairIndex)) /
                        100 /
                        PRECISION,
                    (v.levPosDai *
                        aggregator.pairsStorage().pairNftLimitOrderFeeP(
                            t.pairIndex
                        )) /
                        100 /
                        PRECISION
                );

                emit MarketExecuted(
                    a.orderId,
                    t,
                    false,
                    a.price,
                    0,
                    v.posDai,
                    v.profitP,
                    v.daiSentToTrader
                );
            } else {
                // Gov fee to pay for oracle cost
                // uint govFee = _handleGovFees(
                //     t.trader,
                //     t.pairIndex,
                //     v.levPosDai,
                //     t.positionSizeDai > 0
                // );
                // t.initialPosToken -= (govFee * PRECISION) / i.tokenPriceDai;

                storageT.updateTrade(t);
            }
        }

        if (cancelReason != CancelReason.NONE) {
            emit MarketCloseCanceled(
                a.orderId,
                o.trade.trader,
                o.trade.pairIndex,
                o.trade.index,
                cancelReason
            );
        }

        storageT.unregisterPendingMarketOrder(a.orderId, false);
    }

    function executeNftOpenOrderCallback(
        AggregatorAnswer memory a
    ) external onlyPriceAggregator notDone {
        StorageInterfaceV5.PendingNftOrder memory n = storageT
            .reqID_pendingNftOrder(a.orderId);

        CancelReason cancelReason = !storageT.hasOpenLimitOrder(
            n.trader,
            n.pairIndex,
            n.index
        )
            ? CancelReason.NO_TRADE
            : CancelReason.NONE;

        if (cancelReason == CancelReason.NONE) {
            StorageInterfaceV5.OpenLimitOrder memory o = storageT
                .getOpenLimitOrder(n.trader, n.pairIndex, n.index);

            IGNSOracleRewardsV6_4_1.OpenLimitOrderType t = nftRewards
                .openLimitOrderTypes(n.trader, n.pairIndex, n.index);

            cancelReason = (a.high >= o.maxPrice && a.low <= o.maxPrice)
                ? CancelReason.NONE
                : CancelReason.NOT_HIT;

            // Note: o.minPrice always equals o.maxPrice so can use either
            (
                uint priceImpactP,
                uint priceAfterImpact,
                CancelReason _cancelReason
            ) = _openTradePrep(
                    OpenTradePrepInput(
                        cancelReason == CancelReason.NONE ? o.maxPrice : a.open,
                        o.maxPrice,
                        a.open,
                        a.spreadP,
                        o.buy,
                        o.pairIndex,
                        o.positionSize,
                        o.leverage,
                        tradeData[o.trader][o.pairIndex][o.index][
                            TradeType.LIMIT
                        ].maxSlippageP,
                        o.tp,
                        o.sl
                    )
                );

            bool exactExecution = cancelReason == CancelReason.NONE;

            cancelReason = !exactExecution &&
                (
                    o.maxPrice == 0 ||
                        t == IGNSOracleRewardsV6_4_1.OpenLimitOrderType.MOMENTUM
                        ? (o.buy ? a.open < o.maxPrice : a.open > o.maxPrice)
                        : (o.buy ? a.open > o.maxPrice : a.open < o.maxPrice)
                )
                ? CancelReason.NOT_HIT
                : _cancelReason;

            if (cancelReason == CancelReason.NONE) {
                (
                    StorageInterfaceV5.Trade
                        memory finalTrade /*, uint tokenPriceDai */,

                ) = _registerTrade(
                        StorageInterfaceV5.Trade(
                            o.trader,
                            o.pairIndex,
                            0,
                            // 0,
                            o.positionSize,
                            priceAfterImpact,
                            o.buy,
                            o.leverage,
                            o.tp,
                            o.sl
                        ),
                        true,
                        n.index
                    );

                storageT.unregisterOpenLimitOrder(
                    o.trader,
                    o.pairIndex,
                    o.index
                );

                emit LimitExecuted(
                    a.orderId,
                    n.index,
                    finalTrade,
                    n.nftHolder,
                    StorageInterfaceV5.LimitOrder.OPEN,
                    finalTrade.openPrice,
                    priceImpactP,
                    // (finalTrade.initialPosToken * tokenPriceDai) / PRECISION,
                    finalTrade.positionSizeDai,
                    0,
                    0,
                    exactExecution
                );
            }
        }

        if (cancelReason != CancelReason.NONE) {
            emit NftOrderCanceled(
                a.orderId,
                n.nftHolder,
                StorageInterfaceV5.LimitOrder.OPEN,
                cancelReason
            );
        }

        nftRewards.unregisterTrigger(
            IGNSOracleRewardsV6_4_1.TriggeredLimitId(
                n.trader,
                n.pairIndex,
                n.index,
                n.orderType
            )
        );

        storageT.unregisterPendingNftOrder(a.orderId);
    }

    function executeNftCloseOrderCallback(
        AggregatorAnswer memory a
    ) external onlyPriceAggregator notDone {
        StorageInterfaceV5.PendingNftOrder memory o = storageT
            .reqID_pendingNftOrder(a.orderId);
        IGNSOracleRewardsV6_4_1.TriggeredLimitId
            memory triggeredLimitId = IGNSOracleRewardsV6_4_1.TriggeredLimitId(
                o.trader,
                o.pairIndex,
                o.index,
                o.orderType
            );
        StorageInterfaceV5.Trade memory t = _getOpenTrade(
            o.trader,
            o.pairIndex,
            o.index
        );

        AggregatorInterfaceV6_4 aggregator = storageT.priceAggregator();

        CancelReason cancelReason = a.open == 0
            ? CancelReason.MARKET_CLOSED
            : (t.leverage == 0 ? CancelReason.NO_TRADE : CancelReason.NONE);

        if (cancelReason == CancelReason.NONE) {
            StorageInterfaceV5.TradeInfo memory i = _getOpenTradeInfo(
                t.trader,
                t.pairIndex,
                t.index
            );

            PairsStorageInterfaceV6 pairsStored = aggregator.pairsStorage();

            Values memory v;
            // v.levPosDai =
            //     (t.initialPosToken * i.tokenPriceDai * t.leverage) /
            //     PRECISION;
            v.levPosDai = t.positionSizeDai * t.leverage;
            // v.posDai = v.levPosDai / t.leverage;
            v.posDai = t.positionSizeDai;

            if (o.orderType == StorageInterfaceV5.LimitOrder.LIQ) {
                v.liqPrice = borrowingFees.getTradeLiquidationPrice(
                    GNSBorrowingFeesInterfaceV6_4.LiqPriceInput(
                        t.trader,
                        t.pairIndex,
                        t.index,
                        t.openPrice,
                        t.buy,
                        v.posDai,
                        t.leverage
                    )
                );
            }

            v.price = o.orderType == StorageInterfaceV5.LimitOrder.TP
                ? t.tp
                : (
                    o.orderType == StorageInterfaceV5.LimitOrder.SL
                        ? t.sl
                        : v.liqPrice
                );

            v.exactExecution =
                v.price > 0 &&
                a.low <= v.price &&
                a.high >= v.price;

            if (v.exactExecution) {
                v.reward1 = o.orderType == StorageInterfaceV5.LimitOrder.LIQ
                    ? (v.posDai * 5) / 100
                    : (v.levPosDai *
                        pairsStored.pairNftLimitOrderFeeP(t.pairIndex)) /
                        100 /
                        PRECISION;
            } else {
                v.price = a.open;

                v.reward1 = o.orderType == StorageInterfaceV5.LimitOrder.LIQ
                    ? (
                        (t.buy ? a.open <= v.liqPrice : a.open >= v.liqPrice)
                            ? (v.posDai * 5) / 100
                            : 0
                    )
                    : (
                        ((o.orderType == StorageInterfaceV5.LimitOrder.TP &&
                            t.tp > 0 &&
                            (t.buy ? a.open >= t.tp : a.open <= t.tp)) ||
                            (o.orderType == StorageInterfaceV5.LimitOrder.SL &&
                                t.sl > 0 &&
                                (t.buy ? a.open <= t.sl : a.open >= t.sl)))
                            ? (v.levPosDai *
                                pairsStored.pairNftLimitOrderFeeP(
                                    t.pairIndex
                                )) /
                                100 /
                                PRECISION
                            : 0
                    );
            }

            cancelReason = v.reward1 == 0
                ? CancelReason.NOT_HIT
                : CancelReason.NONE;

            // If can be triggered
            if (cancelReason == CancelReason.NONE) {
                v.profitP = _currentPercentProfit(
                    t.openPrice,
                    v.price,
                    t.buy,
                    t.leverage
                );
                // v.tokenPriceDai = aggregator.tokenPriceDai();

                v.daiSentToTrader = _unregisterTrade(
                    t,
                    false,
                    v.profitP,
                    v.posDai,
                    i.openInterestDai,
                    o.orderType == StorageInterfaceV5.LimitOrder.LIQ
                        ? v.reward1
                        : (v.levPosDai *
                            pairsStored.pairCloseFeeP(t.pairIndex)) /
                            100 /
                            PRECISION,
                    v.reward1
                );

                // _handleOracleRewards(
                //     triggeredLimitId,
                //     t.trader,
                //     (v.reward1 * 2) / 10,
                //     v.tokenPriceDai
                // );

                emit LimitExecuted(
                    a.orderId,
                    o.index,
                    t,
                    o.nftHolder,
                    o.orderType,
                    v.price,
                    0,
                    v.posDai,
                    v.profitP,
                    v.daiSentToTrader,
                    v.exactExecution
                );
            }
        }

        if (cancelReason != CancelReason.NONE) {
            emit NftOrderCanceled(
                a.orderId,
                o.nftHolder,
                o.orderType,
                cancelReason
            );
        }

        nftRewards.unregisterTrigger(triggeredLimitId);
        storageT.unregisterPendingNftOrder(a.orderId);
    }

    // Shared code between market & limit callbacks
    function _registerTrade(
        StorageInterfaceV5.Trade memory trade,
        bool isLimitOrder,
        uint limitIndex
    ) private returns (StorageInterfaceV5.Trade memory, uint) {
        AggregatorInterfaceV6_4 aggregator = storageT.priceAggregator();
        PairsStorageInterfaceV6 pairsStored = aggregator.pairsStorage();

        Values memory v;

        v.levPosDai = trade.positionSizeDai * trade.leverage;
        // v.tokenPriceDai = aggregator.tokenPriceDai();

        // 1. Charge referral fee (if applicable) and send DAI amount to vault
        // if (referrals.getTraderReferrer(trade.trader) != address(0)) {
        //     // Use this variable to store lev pos dai for dev/gov fees after referral fees
        //     // and before volumeReferredDai increases
        //     v.posDai =
        //         (v.levPosDai *
        //             (100 *
        //                 PRECISION -
        //                 referrals.getPercentOfOpenFeeP(trade.trader))) /
        //         100 /
        //         PRECISION;

        //     v.reward1 = referrals.distributePotentialReward(
        //         trade.trader,
        //         v.levPosDai,
        //         pairsStored.pairOpenFeeP(trade.pairIndex),
        //         v.tokenPriceDai
        //     );

        //     _sendToVault(v.reward1, trade.trader);
        //     trade.positionSizeDai -= v.reward1;

        //     emit ReferralFeeCharged(trade.trader, v.reward1);
        // }

        // 2. Calculate gov fee (- referral fee if applicable)
        uint govFee = _handleGovFees(
            trade.trader,
            trade.pairIndex,
            (v.posDai > 0 ? v.posDai : v.levPosDai),
            true
        );
        v.reward1 = govFee; // SSS fee (previously dev fee)

        // 3. Calculate Market/Limit fee
        v.reward2 =
            (v.levPosDai * pairsStored.pairNftLimitOrderFeeP(trade.pairIndex)) /
            100 /
            PRECISION;

        // 3.1 Deduct gov fee, SSS fee (previously dev fee), Market/Limit fee
        trade.positionSizeDai -= govFee + v.reward1 + v.reward2;

        // 3.2 Distribute Oracle fee and send DAI amount to vault if applicable
        if (isLimitOrder) {
            v.reward3 = (v.reward2 * 2) / 10; // 20% of limit fees
            _sendToVault(v.reward3, trade.trader);

            // _handleOracleRewards(
            //     IGNSOracleRewardsV6_4_1.TriggeredLimitId(
            //         trade.trader,
            //         trade.pairIndex,
            //         limitIndex,
            //         StorageInterfaceV5.LimitOrder.OPEN
            //     ),
            //     trade.trader,
            //     v.reward3,
            //     v.tokenPriceDai
            // );
        }

        // 3.3 Distribute SSS fee (previous dev fee + market/limit fee - oracle reward)
        _distributeStakingReward(
            trade.trader,
            v.reward1 + v.reward2 - v.reward3
        );

        // 4. Set trade final details
        trade.index = storageT.firstEmptyTradeIndex(
            trade.trader,
            trade.pairIndex
        );
        // trade.initialPosToken =
        //     (trade.positionSizeDai * PRECISION) /
        //     v.tokenPriceDai;

        trade.tp = _correctTp(
            trade.openPrice,
            trade.leverage,
            trade.tp,
            trade.buy
        );
        trade.sl = _correctSl(
            trade.openPrice,
            trade.leverage,
            trade.sl,
            trade.buy
        );

        // 5. Call other contracts
        pairInfos.storeTradeInitialAccFees(
            trade.trader,
            trade.pairIndex,
            trade.index,
            trade.buy
        );
        pairsStored.updateGroupCollateral(
            trade.pairIndex,
            trade.positionSizeDai,
            trade.buy,
            true
        );
        borrowingFees.handleTradeAction(
            trade.trader,
            trade.pairIndex,
            trade.index,
            trade.positionSizeDai * trade.leverage,
            true,
            trade.buy
        );

        // 6. Store final trade in storage contract
        storageT.storeTrade(
            trade,
            StorageInterfaceV5.TradeInfo(
                0,
                // v.tokenPriceDai,
                trade.positionSizeDai * trade.leverage,
                0,
                0,
                false
            )
        );

        // 7. Store tradeLastUpdated
        LastUpdated storage lastUpdated = tradeLastUpdated[trade.trader][
            trade.pairIndex
        ][trade.index][TradeType.MARKET];
        uint32 currBlock = uint32(ChainUtils.getBlockNumber());
        lastUpdated.tp = currBlock;
        lastUpdated.sl = currBlock;
        lastUpdated.created = currBlock;

        return (
            trade,
            //  v.tokenPriceDai
            0
        );
    }

    function _unregisterTrade(
        StorageInterfaceV5.Trade memory trade,
        bool marketOrder,
        int percentProfit, // PRECISION
        uint currentDaiPos, // 1e18
        uint openInterestDai, // 1e18
        uint closingFeeDai, // 1e18
        uint nftFeeDai // 1e18 (= SSS reward if market order)
    ) private returns (uint daiSentToTrader) {
        IGToken vault = storageT.vault();

        // 1. Calculate net PnL (after all closing and holding fees)
        (daiSentToTrader, ) = _getTradeValue(
            trade,
            currentDaiPos,
            percentProfit,
            closingFeeDai + nftFeeDai
        );

        // 2. Calls to other contracts
        borrowingFees.handleTradeAction(
            trade.trader,
            trade.pairIndex,
            trade.index,
            openInterestDai,
            false,
            trade.buy
        );
        _getPairsStorage().updateGroupCollateral(
            trade.pairIndex,
            openInterestDai / trade.leverage,
            trade.buy,
            false
        );

        // 3. Unregister trade from storage
        storageT.unregisterTrade(trade.trader, trade.pairIndex, trade.index);

        // 4.1 If collateral in storage
        if (trade.positionSizeDai > 0) {
            Values memory v;

            // 5. DAI vault reward
            v.reward2 = (closingFeeDai * daiVaultFeeP) / 100;
            _transferFromStorageToAddress(address(this), v.reward2);
            vault.distributeReward(v.reward2);

            emit DaiVaultFeeCharged(trade.trader, v.reward2);

            // 6. SSS reward
            v.reward3 =
                (marketOrder ? nftFeeDai : (nftFeeDai * 8) / 10) +
                (closingFeeDai * sssFeeP) /
                100;
            _distributeStakingReward(trade.trader, v.reward3);

            // 7. Take DAI from vault if winning trade
            // or send DAI to vault if losing trade
            uint daiLeftInStorage = currentDaiPos - v.reward3 - v.reward2;

            if (daiSentToTrader > daiLeftInStorage) {
                vault.sendAssets(
                    daiSentToTrader - daiLeftInStorage,
                    trade.trader
                );
                _transferFromStorageToAddress(trade.trader, daiLeftInStorage);
            } else {
                _sendToVault(daiLeftInStorage - daiSentToTrader, trade.trader);
                _transferFromStorageToAddress(trade.trader, daiSentToTrader);
            }

            // 4.2 If collateral in vault, just send dai to trader from vault
        } else {
            vault.sendAssets(daiSentToTrader, trade.trader);
        }
    }

