contracts/libraries/PackingUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

library PackingUtils {
    function pack(
        uint256[] memory values,
        uint256[] memory bitLengths
    ) external pure returns (uint256 packed) {
        require(
            values.length == bitLengths.length,
            "Mismatch in the lengths of values and bitLengths arrays"
        );

        uint256 currentShift;

        for (uint256 i; i < values.length; i++) {
            require(
                currentShift + bitLengths[i] <= 256,
                "Packed value exceeds 256 bits"
            );

            uint256 maxValue = (1 << bitLengths[i]) - 1;
            require(
                values[i] <= maxValue,
                "Value too large for specified bit length"
            );

            uint256 maskedValue = values[i] & maxValue;
            packed |= maskedValue << currentShift;
            currentShift += bitLengths[i];
        }
    }

    function unpack(
        uint256 packed,
        uint256[] memory bitLengths
    ) external pure returns (uint256[] memory values) {
        values = new uint256[](bitLengths.length);

        uint256 currentShift;
        for (uint256 i; i < bitLengths.length; i++) {
            require(
                currentShift + bitLengths[i] <= 256,
                "Unpacked value exceeds 256 bits"
            );

            uint256 maxValue = (1 << bitLengths[i]) - 1;
            uint256 mask = maxValue << currentShift;
            values[i] = (packed & mask) >> currentShift;

            currentShift += bitLengths[i];
        }
    }

    function unpack256To64(
        uint256 packed
    ) external pure returns (uint64 a, uint64 b, uint64 c, uint64 d) {
        a = uint64(packed);
        b = uint64(packed >> 64);
        c = uint64(packed >> 128);
        d = uint64(packed >> 192);
    }

    // Function-specific unpacking utils
    function unpackExecuteNftOrder(
        uint256 packed
    )
        external
        pure
        returns (
            uint256 a,
            address b,
            uint256 c,
            uint256 d,
            uint256 e,
            uint256 f
        )
    {
        a = packed & 0xFF; // 8 bits
        b = address(uint160(packed >> 8)); // 160 bits
        c = (packed >> 168) & 0xFFFF; // 16 bits
        d = (packed >> 184) & 0xFFFF; // 16 bits
        e = (packed >> 200) & 0xFFFF; // 16 bits
        f = (packed >> 216) & 0xFFFF; // 16 bits
    }
}

contracts/libraries/TradeUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "../interfaces/StorageInterfaceV5.sol";

library TradeUtils {
    function _getTradeLastUpdated(
        address _callbacks,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksV6_4.TradeType _type
    )
        internal
        view
        returns (
            TradingCallbacksV6_4,
            TradingCallbacksV6_4.LastUpdated memory,
            TradingCallbacksV6_4.SimplifiedTradeId memory
        )
    {
        TradingCallbacksV6_4 callbacks = TradingCallbacksV6_4(_callbacks);
        TradingCallbacksV6_4.LastUpdated memory l = callbacks.tradeLastUpdated(
            trader,
            pairIndex,
            index,
            _type
        );

        return (
            callbacks,
            l,
            TradingCallbacksV6_4.SimplifiedTradeId(
                trader,
                pairIndex,
                index,
                _type
            )
        );
    }

    function setTradeLastUpdated(
        address _callbacks,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksV6_4.TradeType _type,
        uint blockNumber
    ) external {
        uint32 b = uint32(blockNumber);
        TradingCallbacksV6_4 callbacks = TradingCallbacksV6_4(_callbacks);
        callbacks.setTradeLastUpdated(
            TradingCallbacksV6_4.SimplifiedTradeId(
                trader,
                pairIndex,
                index,
                _type
            ),
            TradingCallbacksV6_4.LastUpdated(b, b, b, b)
        );
    }

    function setSlLastUpdated(
        address _callbacks,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksV6_4.TradeType _type,
        uint blockNumber
    ) external {
        (
            TradingCallbacksV6_4 callbacks,
            TradingCallbacksV6_4.LastUpdated memory l,
            TradingCallbacksV6_4.SimplifiedTradeId memory id
        ) = _getTradeLastUpdated(_callbacks, trader, pairIndex, index, _type);

        l.sl = uint32(blockNumber);
        callbacks.setTradeLastUpdated(id, l);
    }

    function setTpLastUpdated(
        address _callbacks,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksV6_4.TradeType _type,
        uint blockNumber
    ) external {
        (
            TradingCallbacksV6_4 callbacks,
            TradingCallbacksV6_4.LastUpdated memory l,
            TradingCallbacksV6_4.SimplifiedTradeId memory id
        ) = _getTradeLastUpdated(_callbacks, trader, pairIndex, index, _type);

        l.tp = uint32(blockNumber);
        callbacks.setTradeLastUpdated(id, l);
    }

    function setTradeData(
        address _callbacks,
        address trader,
        uint pairIndex,
        uint index,
        TradingCallbacksV6_4.TradeType _type,
        uint maxSlippageP
    ) external {
        require(maxSlippageP <= type(uint40).max, "OVERFLOW");
        TradingCallbacksV6_4 callbacks = TradingCallbacksV6_4(_callbacks);
        callbacks.setTradeData(
            TradingCallbacksV6_4.SimplifiedTradeId(
                trader,
                pairIndex,
                index,
                _type
            ),
            TradingCallbacksV6_4.TradeData(uint40(maxSlippageP), 0)
        );
    }
}

